insert  into `wm_news`(`id`,`user_id`,`title`,`content`,`type`,`channel_id`,`labels`,`created_time`,`submited_time`,`status`,`publish_time`,`reason`,`article_id`,`images`,`enable`) values (6269,1102,'两明星晒同款情侣钻戒','[{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/c161c26e571b4de3ac58d26fd9093d29.png\"},{\"type\":\"text\",\"value\":\"ds发射点发射点反对\"}]',1,6,'11','2021-11-26 11:37:38','2021-11-26 11:37:38',9,'2021-11-23 10:27:08','审核通过',1464075849438355457,'material/2021/11/20211123/c161c26e571b4de3ac58d26fd9093d29.png',1),(6270,1102,'测试自动审核','[{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/c8a34fdabe4949258f00545ab13d181d.png\"},{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/72ba84a84f0e461e865b737759d1ddf4.jpg\"},{\"type\":\"text\",\"value\":\"啊  真好\"}]',1,6,'明星','2021-11-26 11:37:31','2021-11-26 11:37:31',9,'2021-11-23 14:28:34','图片中有不确定因素，需要人工审核',1464138455859617793,'material/2021/11/20211123/c8a34fdabe4949258f00545ab13d181d.png',1),(6271,1102,'测试自动审核2','[{\"type\":\"text\",\"value\":\"看看能不能有bug\"},{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/8832323cc9064d5b804f7f97d06a67ab.jpg\"},{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/470666debc8946dca898ce1ec4f3761f.jpg\"}]',1,6,'明星','2021-11-26 11:37:20','2021-11-26 11:37:20',9,'2021-11-23 15:16:31','审核通过',1464075776423911426,'material/2021/11/20211123/8832323cc9064d5b804f7f97d06a67ab.jpg',1),(6272,1102,'测试自动发布','[{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/c8a34fdabe4949258f00545ab13d181d.png\"},{\"type\":\"text\",\"value\":\"dsfsdfsdfsad\"}]',1,7,'明星','2021-11-26 11:37:15','2021-11-26 11:37:15',9,'2021-11-24 11:27:47','审核通过',1464075753590120450,'material/2021/11/20211123/c8a34fdabe4949258f00545ab13d181d.png',1),(6273,1102,'测试发布文章2','[{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/b8cc1924f6774cf195e09c35d234835b.jpg\"},{\"type\":\"text\",\"value\":\"法大师傅士大夫\"}]',1,7,'明星','2021-11-26 11:36:00','2021-11-26 11:36:00',9,'2021-11-24 11:30:10','审核通过',1464075443576528898,'material/2021/11/20211123/b8cc1924f6774cf195e09c35d234835b.jpg',1),(6274,1102,'大师傅士大夫','[{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/72ba84a84f0e461e865b737759d1ddf4.jpg\"},{\"type\":\"text\",\"value\":\"dfgfdg\"}]',1,6,'测试','2021-11-26 11:35:54','2021-11-26 11:35:54',9,'2021-11-24 11:30:56','图片中有不确定因素，需要人工审核',1464138456350351362,'material/2021/11/20211123/72ba84a84f0e461e865b737759d1ddf4.jpg',1),(6275,1102,'于正新剧虐杀猫？剧方回应：确保安全的前提下拍摄','[{\"type\":\"text\",\"value\":\"网易娱乐11月26日报道 近日，《当家主母》中有把猫毒死的镜头，有网友质疑是用了真猫，将小猫虐待致死。11月26日，《当家主母》剧组就今日热议关于猫死亡镜头回应，剧组将具体的拍摄手法以及拍摄花絮公布，称小猫挣扎的画面是工作人员在不会伤害猫咪的情况下，用绳子牵引完成的；猫倒地的画面实际上是猫咪在舔舐可食用血浆＋糖浆。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"剧组称该猫视频中的小猫叫“咪咪”，是土生土长的横店猫。可爱、专业。是整个摄制组爱猫人士的团宠。还表示猫是人类的好朋友，我们无比重视其安全，并将在后续更加重视对动物的保护和爱护，非常感谢网友监督，也感谢网友对《当家主母》的支持。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：刘舒阳_NBJS16803)\"}]',0,6,'爬虫','2021-11-26 15:41:30','2021-11-26 15:41:30',9,'2021-11-26 15:41:30','阿里云调用失败，转人工审核',1464138456568455170,'',1),(6276,1102,'好继母！章子怡晒家人大合照 为小苹果庆16岁生日','[{\"type\":\"text\",\"value\":\"网易娱乐11月26日报道 11月25日晚，章子怡在微博发文为小苹果庆16岁生日，“我们的大女儿16岁了！Sweet 16，花季少女啦！希望你永远貌如花般美丽，生如花般灿烂！心中永存感恩与善念，平安、快乐，生日快乐宝贝。”照片中，汪峰章子怡一家四口同框，小苹果在c位捧着蛋糕，画面温馨幸福。\"},{\"type\":\"text\",\"value\":\"此前报道：\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"汪峰父女现身章子怡电影首映 大女儿表白：妈你辛苦了\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"9月29日， 章子怡 、徐峥、黄渤、吴京四位导演现身电影《我和我的父辈》首映礼，和观众朋友们畅聊荧幕背后那些有趣又艰难的创作故事。\"},{\"type\":\"text\",\"value\":\"这是章子怡作为导演的处女秀首映礼， 汪峰 也带着大女儿“小苹果”来现场为章子怡加油助威。汪峰还在采访中透露，章子怡由于对电影要求过高，来来回回将剧本改动近30次，拍摄过程中曾经也一度想要放弃，但最后她还是坚持了下来。\"},{\"type\":\"text\",\"value\":\"这次大女儿“小苹果”的公开露面也令网友们感到惊喜，今年16岁的她已经出落得十分漂亮，五官与汪峰神似，身材高挑。\"},{\"type\":\"text\",\"value\":\"因为不常出席这样的活动，小苹果在接受采访时还有些羞涩，汪峰在一旁拍拍她的肩，示意她放轻松一些。\"},{\"type\":\"text\",\"value\":\"在小苹果站起来发言前，章子怡就先对着大家公开介绍到，“这是我家女儿”，言语中尽显宠溺。\"},{\"type\":\"text\",\"value\":\"小苹果虽然有些紧张，但仍不忘表白章子怡，表示自己观影后十分感动，对着章子怡说“妈辛苦了，回家咱吃夜宵”。 首映礼结束后，章子怡朝着观众席汪峰和大女儿的方向用力地挥手，脸上都是幸福的笑容，父女俩也挥手回应她，示意两人先走了，让她安心继续工作。\"},{\"type\":\"text\",\"value\":\"据悉，“小苹果”是汪峰与前妻葛荟婕生的孩子，两人在女儿只有一岁的时候就离婚了。后来，汪峰迎娶了章子怡，然后两人共同抚养“小苹果”，章子怡与继女相处得非常好，平时经常一起逛街，关系就像姐妹一般。\"},{\"type\":\"text\",\"value\":\"此前，章子怡还发微博为大女儿庆祝15岁生日，一家人甜蜜合影。\"},{\"type\":\"text\",\"value\":\"从合照中也能看出，章子怡习惯性的把手搭在大女儿肩上，而小女儿也趴着姐姐腿上十分可爱，看得出来一家人感情非常亲密。\"},{\"type\":\"text\",\"value\":\"此前汪峰和章子怡晒出两人的结婚周年合影，章子怡还在评论区透露，这是大女儿“熙熙宝贝”拍的。\"},{\"type\":\"text\",\"value\":\"虽然章子怡是继母，但是她和小苹果一直相处得非常好，完全是把小苹果视如己出。\"},{\"type\":\"text\",\"value\":\"前段时间章子怡还被偶遇和大女儿一起外出逛街，15岁的小苹果长得比章子怡还高，两人逛街全程手挽着手，就像两姐妹一样。\"},{\"type\":\"text\",\"value\":\"曾经章子怡还在节目中还满脸骄傲跟朋友介绍，大女儿从来没有排斥过她，第一次见面的时候，当时小苹果才8岁，一见到章子怡就喊她妈妈，也是非常懂事了。\"},{\"type\":\"text\",\"value\":\"或许由于小苹果从小就没有获得过很多的母爱，从小都是奶奶照顾长大。而汪峰和章子怡结婚7年以来，章子怡也都特别真心地对待小苹果，让这对“继女”和“后妈”的关系变得不再有隔阂。\"},{\"type\":\"text\",\"value\":\"章子怡也曾表示，自己对大女儿的爱，可能在某种程度上已经超过了亲生母女的感情，“我的大女儿，缺失了母亲的爱。但是我特别幸福，因为我有缘认识了她。有缘成为她的母亲。我要把所有的爱，也许会胜过我的亲生女儿，我都愿意给她……”\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"如今章子怡和汪峰一家五口的生活也是非常幸福，经常在一起甜蜜互动，相信这都是他们用心经营出来的结果！\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：胡梦瑶_NK5655)\"}]',0,6,'爬虫','2021-11-26 15:41:31','2021-11-26 15:41:31',2,'2021-11-26 15:41:31','文本内容包含自管理的敏感词: {55=1}',NULL,'',1),(6277,1102,'秀恩爱！70岁张纪中被小娇妻霸气搂怀里 闭眼大笑','[{\"type\":\"text\",\"value\":\"网易娱乐11月26日报道 近日，张纪中夫妇晒出了两人对口型的甜蜜互动日常，并配文“我说呢，怎么好好的突然变脸了，原来在最后等着我呢”。\"},{\"type\":\"text\",\"value\":\"视频中，张纪中的妻子一头长发及腰，只见她双手环胸坐在沙发上，而张纪中则坐在沙发边缘姿态卑微。妻子假装愤怒的嗔怪张纪中，话里话外都是甜蜜的责备。而张纪中则手足无措，张口想要跟妻子解释，但却总被妻子打断。\"},{\"type\":\"text\",\"value\":\"最后妻子一把把张纪中搂进怀里说“快到我怀里来”。张纪中也一脸幸福地依偎在妻子胸前，抱紧了妻子。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"相差31岁的张纪中夫妇，一直以来都不被大家看好，很多人都嘲讽他们不般配，“老夫少妻”。但是两人丝毫不在意别人的眼光，依旧过自己的幸福日子。\"},{\"type\":\"text\",\"value\":\"此前报道： 张纪中批明星耍大牌:观众才是上帝 最后你成了上帝\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"网易娱乐专稿10月27日报道 （作者|张晶）著名导演、制片人 张纪中 近日在网易娱乐的独家专访中回应不实传闻，否认与妻子打假离婚住院等消息。他坦言，太太没有图他的钱，他也没那么有钱，为了养三个孩子，还得直播带货，而对于“老夫少妻”这样的现状，70岁的自己能给太太最好的东西，就是他本人的健康。作为 娱乐圈 资深的影视制作人，张纪中一向直率敢言，话锋直指今年娱乐圈出现的各种乱象。他首先提到娱乐圈很多人入行没有初心，只想赚钱。原本是文艺界，照亮人们心灵的行业，结果现在的年轻人都被培养成一个被赚钱的工具。他们有好的想法，后边是我怎么多赚点钱，“隐藏起来的实际目的是这个。”\"},{\"type\":\"text\",\"value\":\"今年以来，娱乐圈频频“爆雷”，先是郑爽代孕、日进208万的天价 片酬 ，后是顶流爱豆吴亦凡涉嫌强奸被抓，歌手霍尊言论失格，李云迪嫖娼被抓，这一系列的负面事件，让当局对娱乐圈的行业风气颇为不满。\"},{\"type\":\"text\",\"value\":\"作为“德艺双馨的老艺术家”张纪中，认为目前的“清朗行动”是一个非常好的趋势，而且我们现在整顿，需要标本一起整，首先规则很重要，不能只讲结果。我们说姓郑的女孩，她要了这么多钱，为什么给她？谁给她？它的合理性在哪？后面的操作是怎么样的？\"},{\"type\":\"text\",\"value\":\"他复盘了流量经济背后的成因，对于平台而言，“你有流量我就给你收购”，但是对于制作方而言，“你拍了那么多，都不收购，好，为了播出，你说张三行，我就找张三，比如一共有10家去找了张三，这边我说给一千万，给两千万，三千万，就这样抬价，这些人最终拿到的可能是一个最烂的剧本，但价钱最高。”张纪中说，圈子的规律从艺术规律，转变成“我出钱我说了算”，平台成了规律，流量成为选择 演员 的标准。\"},{\"type\":\"text\",\"value\":\"当然，平台也很难啊，平台依靠流量，没有流量，平台也拉不上广告，所以对平台而言，“我们也是为了挣钱，我们也算一算账。（于是）大家都在这算经济账。”张纪中反问道，平台赚钱吗？得出的结论是“据说都在赔钱”。\"},{\"type\":\"text\",\"value\":\"流量是怎么造成的呢？在张纪中看来，“很多年轻的小朋友在网上盲目地追星，所谓的饭圈，形成一种势，拉低了我们的审美，把我们一些演戏好的老戏骨排斥在外。”这种明显的“劣币驱逐良币”，破坏了整个行业生态。\"},{\"type\":\"text\",\"value\":\"这个现状对于艺人演员来说，却是挣得盆满钵满，之前抬上去的身价，谁都不愿降下来，“等到接第二个戏的时候，你看我原来就拿这么多钱，现在我得更高一点。”张纪中直言，但艺术水准却不见得跟得上。\"},{\"type\":\"text\",\"value\":\"对制作方而言，好不容易“跪着”请来的演员，到了剧组依旧给他跪着，严重影响了剧组的风气，“你到我这儿是摆谱来了，还是干嘛？你来拍戏就给我好好拍戏。这个剧组我不允许这样。”张纪中说，一个剧组如果不树立好的风气，天天跪舔这些人，无法做出好的作品，拍好作品的导演也一定不会这么迁就。\"},{\"type\":\"text\",\"value\":\"对于一些演员“耍大牌”，张纪中更是深恶痛绝，在他担任制片人的从业经历中，也遇到过艺人摆谱的情况，“有些演员提出的这种要求，我要带着我妈，带着我的孩子，我的狗，带着我的助理，那么我一共要六间房，几辆车。”张纪中直接拒绝，“都不知道自己姓什么了，你就是一个演员。”\"},{\"type\":\"text\",\"value\":\"他觉得，一个人拍一部好戏并不难，难的是你每一部都是人家知道的戏。什么是一线演员？名气？片酬？“事实上真正能达到一线的，他们都有很好的作品，陈道明，李雪健……你说哪一个能赶上他们？”张纪中回忆道，杨丽萍当初演梅超风，一个助理都没带，只有自己一个人，拎着箱子就来了，在片场即便没有自己的戏份，也一直在现场待着观摩，十分敬业。\"},{\"type\":\"text\",\"value\":\"张纪中经常告诫身边的年轻人，在可能的情况下，人家跟你照个相，你就跟人照一个，“观众喜欢你，你不要把这种喜欢当成你不可一世的资本，你是因为人家喜欢你才到了今天，那些才是你的上帝，最后你成了上帝，这怎么行？”他希望现在的年轻演员，能多懂一点艺术规律，提高自己的艺术鉴赏力，方能走得长远。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：蒋紫莹_NBJS16943)\"}]',0,6,'爬虫','2021-11-26 15:41:32','2021-11-26 15:41:32',2,'2021-11-26 15:41:32','文本中有违规内容，审核失败',NULL,'',1),(6278,1102,'汪小菲离婚后去张兰直播间卖辣酱 喊话网友刷礼物','[{\"type\":\"text\",\"value\":\"网易娱乐11月26日报道 11月22日，大S和汪小菲发布离婚声明，引发热议。近日，有网友发现汪小菲现身张兰的直播间，与妈妈一起直播卖辣酱，这也是他离婚后的首次公开露面。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"直播中，汪小菲坐在张兰身旁，穿一身黑衣打扮低调，看上去状态不错。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"女强人张兰一如既往活跃，满脸笑容还不断吆喝网友“快点给汪小菲送点礼物”、“嘉年华直接送上来”，气氛十分嗨，似乎不受儿子离婚事件的影响。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：胡梦瑶_NK5655)\"}]',0,6,'爬虫','2021-11-26 15:41:33','2021-11-26 15:41:33',2,'2021-11-26 15:41:33','文本内容包含自管理的敏感词: {55=1}',NULL,'',1),(6279,1102,'《士兵突击》主角聚会 王宝强露灿笑紧搂王大治','[{\"type\":\"text\",\"value\":\"近日，演员高峰在社交平台中晒出了《士兵突击》主演们的私下聚会照，距离该电视剧播出已经过了15年之久，大家没有想到当年的“齐桓”“许二和”“许三多”“袁朗”“高城”在现实生活中仍保持着这么好的关系。\"},{\"type\":\"text\",\"value\":\"画面中的五人从外形来看，大家都成熟了不少，但毕竟是作为娱乐圈艺人，他们的身材外形管理都还算不错，即便上了年纪也仍保持着帅气，段奕宏带着眼镜十分有魅力，王大治多年不变的凌乱发型也十分抢眼。\"},{\"type\":\"text\",\"value\":\"特别是今年已经39岁的王宝强，他已经从草根演员彻底转型成为了实力派艺人，站在C位上时，他仍保持着和从前一般的淳朴笑容，打扮穿着简单又大气，看起来很有文艺范儿。\"},{\"type\":\"text\",\"value\":\"十五年前的《士兵突击》虽然算不上是大制作，但里面的演员个个实力超强，为大家带来了一部非常惊艳的观剧体验，对于很多8090后而言，这部剧甚至仍是不少人的童年记忆。\"},{\"type\":\"text\",\"value\":\"这部电视剧之后，剧中的演员们也都在私下保持着非常不错的友谊，之前段奕宏在接受采访时，王宝强甚至还曾作为嘉宾出镜为兄弟撑台，将荧幕中的友谊完全延伸到了戏外。\"},{\"type\":\"text\",\"value\":\"段奕宏虽然是科班演员出身，但他对于好兄弟王宝强的“野路子”演技却曾多次表示赞赏，即使是一个简单的“抽烟镜头”，段奕宏也会认真的为大家讲解王宝强的表演方式有多出彩，让所有人都能够更加具体的感受到王宝强的优秀。\"},{\"type\":\"text\",\"value\":\"因为娱乐圈的行业特殊性，很多人都觉得在这个圈子里不会有真正的友谊，但事实上，这些优秀的艺人们，除了一起合作共创优秀作品之外，更多的还是相互之间的欣赏和钦佩，如此真挚的感情，很难不令人羡慕。\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:35','2021-11-26 15:41:35',9,'2021-11-26 15:41:35','阿里云调用失败，转人工审核',1464138456832696322,'',1),(6280,1102,'谭咏麟事件升级！对方曝光聊天记录','[{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"谭咏麟是娱乐圈“天王”级别的艺人，在娱乐圈闯荡几十年，不仅有多首代表作，也因为爱岗敬业，被大家称为“谭校长”，是大家心中德艺双馨的老艺术家，不过现年71岁的谭咏麟，近日却陷入负面风波。\"},{\"type\":\"text\",\"value\":\"起因是本月23号晚，一位名叫“悲伤牛肉面”的网友连发三条微博，爆料谭咏麟跟自己女朋友在一起了，该网友女友比谭咏麟小4轮，至少48岁，也就是说该事件女主只有20几岁，该网友指责谭咏麟失德。\"},{\"type\":\"text\",\"value\":\"虽说该网友是指名道姓的爆料，但是凡事要讲证据，用文字阐述显然说服力不足，在网友的要求下，“悲伤牛肉面”晒出第一张图，是一个男子的背影照，照片显然只是截取了一部分，看不出照片中的男子到底是谁，所以这张图算是放了个“寂寞”。\"},{\"type\":\"text\",\"value\":\"在网友“悲伤牛肉面”晒出第一张所谓的证据照后，谭咏麟一方进行反击，正面辟谣了传闻，称谭咏麟很尊重粉丝，不过对于吃瓜群众来讲，娱乐圈艺人的声明公信度已经被打上问号了。\"},{\"type\":\"text\",\"value\":\"“悲伤牛肉面” 在谭咏麟发声明后，立刻晒出第二波证据图，这次依旧是背影，不过是全身照，可以看到照片中的男子身处酒店，穿着一身运动衣。\"},{\"type\":\"text\",\"value\":\"男子戴着眼镜，腿比较健壮，是常年保持健身的状态，虽说照片看不到正脸，但是戴眼镜、健身这细节跟谭咏麟都很像。\"},{\"type\":\"text\",\"value\":\"24号晚，“悲伤牛肉面”又晒出第三波证据，称自己发的图都是去年的，照片是从女友隐藏相册里看到的，并且放话，只要谭咏麟声明一次他就放一次锤，也让对方不要用资本来压制他。\"},{\"type\":\"text\",\"value\":\"这第三波证据，是另一视角的酒店照，照片中男子正坐在床边穿衣服，这个视角显然是躺在床上的人拍的，算是比较私密的照片了。\"},{\"type\":\"text\",\"value\":\"男子是侧颜，虽然看不到正脸，但是从发型跟衣着看，这三张照片中的男子是同一个人。\"},{\"type\":\"text\",\"value\":\"网友“悲伤牛肉面”也曝光了女友的聊天记录，称其已经把两人对话删得只剩语音，跟女友聊天的男子网名叫“谭25谭”，头像则是谭咏麟年轻时候的照片，不过这也不能确认跟他女友聊天的就是谭咏麟，毕竟网名、头像谁都可以用，从聊天记录看，两人今年10月份联系频繁，通话时长都不短。\"},{\"type\":\"text\",\"value\":\"虽说“悲伤牛肉面”晒出三波证据，但是依旧没有实质性的实锤，不过网友从谭咏麟去年的晒照中发现，他穿过证据照男子中的同款衣服跟鞋子。\"},{\"type\":\"text\",\"value\":\"谭咏麟去年10月份晒的这张照片中，衣服裤子以及运动鞋，跟“悲伤牛肉面”曝光的背影照里的男子衣着几乎一模一样，是巧合？还是什么？\"},{\"type\":\"text\",\"value\":\"目前“悲伤牛肉面”已经隐藏了所晒照片，微博显示有8条动态，但是公开的只有5条文字版，显然是隐藏了照片，是打算息事宁人，不再硬刚了？\"},{\"type\":\"text\",\"value\":\"作为吃瓜群众，静待后续吧！\"},{\"type\":\"text\",\"value\":\"【注：本文为作者原创，欢迎转发文章，订阅关注，未经授权，转载必究！图片来源于网络，如涉及版权请联系删除】\"},{\"type\":\"text\",\"value\":\"延伸阅读：\"},{\"type\":\"text\",\"value\":\"情史复杂风流成性的\\\"谭校长\\\"\"},{\"type\":\"text\",\"value\":\"谭咏麟在音乐、影视上的成就大家有目共睹，自然不必多说。可比起他的演艺成绩，他复杂的情史更为人所津津乐道，极具争议。\"},{\"type\":\"text\",\"value\":\"谭咏麟与发妻杨洁薇相识相恋于微时，那时候的谭咏麟还是个不出名的毛头小子。1981年，情到深处的两人远赴美国拉斯维加斯秘密结婚。\"},{\"type\":\"text\",\"value\":\"为了爱人的梦想与前程，杨洁薇放弃了自己的一切，包括工作与生孩子，转当谭咏麟的经纪人，心甘情愿地成为谭咏麟背后的女人，默默地支持着自己心爱的丈夫。\"},{\"type\":\"text\",\"value\":\"在妻子的扶持下，谭咏麟开始爆红成为巨星，星途一片璀璨。\"},{\"type\":\"text\",\"value\":\"直到1993年，杨洁薇的父亲去世，谭咏麟以“女婿”的身份登上岳父的讣告，两人隐瞒了12年的关系才浮出水面。\"},{\"type\":\"text\",\"value\":\"当外界都指责谭咏麟，深陷舆论中心之时，是杨洁薇挺身而出，挡下了所有的流言蜚语，坚称这一切都是自己的主意，与她心爱的人无关。\"},{\"type\":\"text\",\"value\":\"但此时的谭咏麟，或许已不在意发妻的奉献与付出。因为41岁的谭咏麟早在两年前就背叛了发妻移情别恋，与当时才19岁的女大学生朱咏婷偷偷交往。\"},{\"type\":\"text\",\"value\":\"两人秘恋5年后，朱咏婷还为谭咏麟生下了一个儿子。谭咏麟也从此开启了在两个女人之间周旋的生活。\"},{\"type\":\"text\",\"value\":\"尽管谭咏麟为了自己的演艺事业，对此事极度保密，但纸终究是包不住火的。随着孩子越来越大，媒体也拍到了一家三口庆生的照片。“两女共侍一夫”的操作让外界大为震撼。\"},{\"type\":\"text\",\"value\":\"2006年，谭咏麟的父亲过世，他终于以父亲遗愿为“挡箭牌”，让10多年不得名分的母子俩认祖归宗，公开承认了私生子，等同扶正“小三”朱咏婷。\"},{\"type\":\"text\",\"value\":\"自此之后，就几乎只能看到他们三人的天伦之乐，杨洁薇鲜少再露面。\"},{\"type\":\"text\",\"value\":\"或许是“一片深情喂了狗”让杨洁薇痛彻心扉，彻底伤透。这些年，坊间一直有传她已经出家，遁入空门，港媒也拍到了她现身寺庙的照片。\"},{\"type\":\"text\",\"value\":\"堂堂正宫惨被抛弃，最后竟落到如此下场，实在让人唏嘘。\"},{\"type\":\"text\",\"value\":\"谭咏麟“左拥右抱”的这段经历实在让人印象深刻，风流成性几乎成了“吃瓜群众”对他的刻板印象。\"},{\"type\":\"text\",\"value\":\"本以为这些年谭咏麟与朱咏婷出双入对、感情稳定，儿子也大学毕业，他早已“改邪归正”。此次他却再遭爆料与23岁女粉丝发生不正当关系，让人难免心生怀疑。\"},{\"type\":\"text\",\"value\":\"算起来，这位23岁女粉丝要比他的儿子年纪还小。若是真的，也难怪爆料人质问其：“怎么下得去手？\"},{\"type\":\"text\",\"value\":\"结语\"},{\"type\":\"text\",\"value\":\"尽管谭咏麟的演艺成绩斐然，但私生活确实算不上“光彩”，遭到非议也在情理之中。\"},{\"type\":\"text\",\"value\":\"俗话说：“身正不怕影子斜。”作为一个公众艺人，要想不被负面新闻缠身，那就得洁身自好，爱惜自己的羽毛。\"},{\"type\":\"text\",\"value\":\"不过说到底，此次爆料的真实性仍有待考证。到底是网友恶意造谣？还是确有此事？或许真的只有当事人自己才知道了。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"谭咏麟此次能迈过“出轨罗生门”这道坎吗？期待着事情水落石出的那天。\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"},{\"type\":\"text\",\"value\":\"(责任编辑：史建磊_NBJ11331)\"}]',0,6,'爬虫','2021-11-26 15:41:36','2021-11-26 15:41:36',2,'2021-11-26 15:41:36','文本内容包含自管理的敏感词: {33=1}',NULL,'',1),(6281,1102,'状态超好！45岁林心如穿黑色西服秀美腿','[{\"type\":\"text\",\"value\":\"测试文章内容\"}]',0,6,'爬虫','2021-11-26 15:41:37','2021-11-26 15:41:37',9,'2021-11-26 15:41:37','阿里云调用失败，转人工审核',1464138457025634305,'',1),(6282,1102,'赵丽颖冬日暖阳大片曝光 长发披肩气质温柔','[{\"type\":\"text\",\"value\":\"测试文章内容\"}]',0,6,'爬虫','2021-11-26 15:41:38','2021-11-26 15:41:38',9,'2021-11-26 15:41:38','阿里云调用失败，转人工审核',1464138457235349505,'',1),(6283,1102,'林依晨产后复出状态好 自曝一个月减肥18斤','[{\"type\":\"text\",\"value\":\"网易娱乐11月26日报道 11月25日，林依晨在今年十月份产后首次在某奢侈品牌活动上亮相。她身穿金色亮片刺绣抹胸礼服，在活动现场笑容满满，尽显好气色。\"},{\"type\":\"text\",\"value\":\"在品牌活动中，林依晨状态极佳，面对全方面的闪光镜头时，脸上露出拥有自己孩子后的甜蜜笑容。从照片看出，林依晨的脸部胶原蛋白饱满，锁骨十分显现。她的肌肤在灯光的照射下看起来依旧弹性十足，透嫩反光。\"},{\"type\":\"text\",\"value\":\"在活动中，林依晨透露到，自己在一个月左右的时间内瘦了18斤。如此迅速的瘦身速度实在让人佩服，难怪这种收腰礼服也能被林依晨穿出产前的韵味。如今的身材也完全看不出是刚生产完一个孩子的母亲。\"},{\"type\":\"text\",\"value\":\"她还分享到，自己能够瘦身如此迅速虽然胖的原因大部分是因为水分，还有一部分原因是因为带孩子对她来说很锻炼人，可能消耗了太多的体力。\"},{\"type\":\"text\",\"value\":\"前不久，有媒体晒出林依晨婆婆现身月子中心探望的照片，当时婆婆乘坐豪车，带着大包小包出现在路边，升级成为奶奶的她，想必为了儿媳和孙女也免不了一番操劳。\"},{\"type\":\"text\",\"value\":\"林依晨在十月中旬生出女儿，也就是说她刚刚坐完月子就开始复出工作，不得不承认，作为一个女艺人她实在有些拼命。\"},{\"type\":\"text\",\"value\":\"结婚7年，林依晨一直都面临着备孕艰难的困扰，之前在接受媒体采访时，她也曾多次表示，自己已经尝试过了所有的备孕方式，只是奈何运气不佳。此次不仅平安生产，身材还恢复得如此完美，实在是幸运。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"作为一个已经39岁的女艺人，林依晨平安生产之后，能够如此迅速恢复身材，也属实有些不容易，升级为妈妈之后，想必她也有了新的人生体悟，很期待林依晨接下来能够以全新的身份挑战更多新的角色。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：蒋紫莹_NBJS16943)\"}]',0,6,'爬虫','2021-11-26 15:41:39','2021-11-26 15:41:39',9,'2021-11-26 15:41:39','阿里云调用失败，转人工审核',1464138457377955842,'',1),(6284,1102,'人民日报评《长津湖》登顶：国产主旋律电影的高光','[{\"type\":\"text\",\"value\":\"测试文章内容\"}]',0,6,'爬虫','2021-11-26 15:41:43','2021-11-26 15:41:43',9,'2021-11-26 15:41:43','阿里云调用失败，转人工审核',1464138457537339393,'',1),(6285,1102,'曾经风光如今落魄的港台明星 沦落到跟粉丝借钱','[{\"type\":\"text\",\"value\":\"近年来，因为各种原因，港台娱乐圈的发展远没有内地娱乐圈好，不少香港艺人和台湾艺人也陆续来内地发展甚至定居。有不少曾经很风光的港台明星，如今的情况却变得有些落魄。\"},{\"type\":\"text\",\"value\":\"第一位，万梓良\"},{\"type\":\"text\",\"value\":\"万梓良巅峰时期在港圈和周润发齐名，影帝周星驰和林家栋都曾经受过他的提携照顾。《三少爷的剑》，《江湖情》，《旺角卡门》和《古惑仔》系列令他成为港圈黑帮电影的代表人物之一。\"},{\"type\":\"text\",\"value\":\"如今64岁的他在内地参加各种商演活动仍然有不少粉丝追捧，还在内地收了两个徒弟和一个干女儿。\"},{\"type\":\"text\",\"value\":\"万梓良自然是不缺钱的，有网友爆料，他一场商演就能收8万元报酬，但如果说和他巅峰时期相比，现在万梓良的境况确实有点落魄了。\"},{\"type\":\"text\",\"value\":\"据悉，今年已经64岁的万梓良，在本应该“安享晚年”的年纪却很拼命工作，一边拍戏一边奔波在内地各大城市参加各种商演活动上，这些商演活动很多都是比较小型的，看起来简陋，拍戏也只是拍一些网络大电影。\"},{\"type\":\"text\",\"value\":\"近日，万梓良在好友的社交平台发了一个视频，视频里他透露自己为了出外工作赚钱已经数月没回家了，这次终于可以回家和亲人团聚。\"},{\"type\":\"text\",\"value\":\"今年已经64岁的万梓良早前因为糖尿病身体状况一度变得很差，虽然现在身体恢复了健康，但状态看起来是肉眼可见地变老了很多。\"},{\"type\":\"text\",\"value\":\"他这个年纪还拼命赚钱很大可能是为了让妻子和唯一的儿子以后能过上好日子，毕竟他的现任妻子才48岁，儿子也才18岁。\"},{\"type\":\"text\",\"value\":\"第二位，徐怀钰\"},{\"type\":\"text\",\"value\":\"徐怀钰这个名字算是一代人的记忆，也是一代人的青春。20岁的徐怀钰已经凭借《 我是女生》红极一时，巅峰时期一年为公司赚2亿，一个人养活大半个唱片公司，是继张惠妹之后的第二个“百万偶像”，被亲切地称为“平民天后”。\"},{\"type\":\"text\",\"value\":\"但徐怀钰红得快落魄得也很快，徐怀钰在事业巅峰期时被经纪公司的人指责“耍大牌”，接着她的爷爷说徐怀钰对老人家不闻不问，种种负面传闻让徐怀钰名誉受损，从万人追捧变成无人问津。\"},{\"type\":\"text\",\"value\":\"等徐怀钰再度复出的时候，却因为和经纪公司打官司导致再次没有工作，不但没有工作还欠债百万，最后徐怀钰不得不向经纪公司“认输”，求对方“放过”自己。\"},{\"type\":\"text\",\"value\":\"在徐怀钰最惨的时候，要靠粉丝接济自己，甚至跟粉丝借钱。\"},{\"type\":\"text\",\"value\":\"如今43岁的徐怀钰总算熬过了那段艰难的岁月，但重返娱乐圈的她早已经没有属于她的“位置”，她错过了几年的事业黄金期，如今只能在靠着之前积累的人气，在内地小县城接一些小型商演。\"},{\"type\":\"text\",\"value\":\"不过从徐怀钰的近照来看，她的状态保养得很好。\"},{\"type\":\"text\",\"value\":\"第三位，王杰\"},{\"type\":\"text\",\"value\":\"在香港“四大天王”之前，港圈乐坛的“天王”还是王杰，这个先后在台湾，香港爆红的“浪子歌王”，他的《一场游戏一场梦》，《谁明浪子心》至今仍是经典名曲。\"},{\"type\":\"text\",\"value\":\"多年后王杰在节目中还直言张学友不配叫“歌神”，“四大天王”根本入不了他的眼，可见，王杰当年的人气。\"},{\"type\":\"text\",\"value\":\"在事业巅峰期间，王杰却因为身体状况不好的原因，无奈暂时退出工作，因为医生已经说了，再不停止工作后果很严重。\"},{\"type\":\"text\",\"value\":\"之后王杰再度复出，虽然人气依旧，但随着乐坛新星的出现，他不再是乐坛的“独宠”。事业逐渐走向没落的王杰，之后围绕在他身上的话题只剩下了关于嗓子坏了的传闻。\"},{\"type\":\"text\",\"value\":\"但因为他几次说的话里的一些关键信息是对不上了，导致被人怀疑他说的话是否真实。王杰一时间说自己喝了那杯饮料之后，时隔四五个小时，突然间嗓子就没了。但之后又说自己喝完饮料，一个小时嗓子没有了。\"},{\"type\":\"text\",\"value\":\"除了“嗓子”事件，王杰也说过自己被同行“排挤”。\"},{\"type\":\"text\",\"value\":\"关于“嗓子”的事情如今已经告一段落了，而59岁的王杰也告别了舞台的辉煌，据传已经移居国外。在他曝光的近照里，王杰的两鬓花白，留着花白的胡渣，模样有点沧桑，已经没有昔日的明星光芒了。\"},{\"type\":\"text\",\"value\":\"第四位，温兆伦\"},{\"type\":\"text\",\"value\":\"说起TVB最风光的视帝莫过于第一位三届视帝罗嘉良，当年罗嘉良凭借台庆剧《流金岁月》获得视帝，在剧里温兆伦饰演他的弟弟，光芒远没有罗嘉良亮眼。\"},{\"type\":\"text\",\"value\":\"但事实上，温兆伦比罗嘉良成名更早。温兆伦在30岁之前已经凭借《我本善良》和《义不容情》成名，“丁有康”的角色更加成为港剧反派经典。年轻时外型出众的温兆伦成为一代男神。\"},{\"type\":\"text\",\"value\":\"温兆伦事业可以说被私生活拖累，据悉，温兆伦感情史十分丰富，除了绯闻女友，有实锤的就有8段，离过两次婚。最离谱的一次是向前女友索要14万分手费。\"},{\"type\":\"text\",\"value\":\"之后温兆伦便离开了TVB，先后到台湾和内地发展。温兆伦在台湾和内地的演艺事业发展很一般，10年后已经变成“过气艺人”，如今较年轻一辈的观众已经不认识他了。\"},{\"type\":\"text\",\"value\":\"今年56岁的温兆伦在内地除了商演也加入了直播大军，之前他还直播买卫生巾，手里拿着卫生巾卖力介绍。\"},{\"type\":\"text\",\"value\":\"近日，他还在直播里大谈做明星不容易，哭诉自己不像其他明星有人帮忙发视频，他说自己的直播间只有几千人人气，明明一开始有3万人的。温兆伦在直播间里的状态不是很好，有网友怀疑他喝醉酒，中途他的妻子也出来捂住他的嘴，阻止他继续说下去。\"},{\"type\":\"text\",\"value\":\"虽然温兆伦为自己惨淡人气忧愁，但其实他如今在内地的生活还是不错的。据悉，温兆伦在内地凭借投资做生意赚了不少钱，生活不成问题。\"},{\"type\":\"text\",\"value\":\"他还在49岁的时候娶了小20岁的内地妻子赵庭，50岁的时候生了一个女儿，收获了一个美满的家庭。\"},{\"type\":\"text\",\"value\":\"第五位，钱小豪\"},{\"type\":\"text\",\"value\":\"成名太早又“自毁”前程的明星不少，钱小豪也是其中一个。武打明星出身的钱小豪16岁就出道，成为张彻弟子，之后凭借“僵尸题材”电影和《太极张三丰》中的“董天宝”红极一时，俊朗的外表使他成为男神级人物。\"},{\"type\":\"text\",\"value\":\"但可惜的是，2000年，钱小豪因为一桩丑闻名声大跌，两年内没人找他再拍戏。\"},{\"type\":\"text\",\"value\":\"而如今58岁的钱小豪已经把事业和生活重心放在内地，只不过属于他的年代已经过去了，而在内地10年始终在“炒冷饭”的钱小豪，只能不断拍口碑一般的网络大电影，更被吐槽是“烂片之王”。\"},{\"type\":\"text\",\"value\":\"感情方面，钱小豪和前妻郭秀云离婚之后，郭秀云带着儿子定居国外，之后还改嫁富商，而钱小豪的儿子钱景峰还跟继父姓，改名庞景峰。\"},{\"type\":\"text\",\"value\":\"不过庞景峰成年之后就回国跟着亲生爸爸钱小豪发展，钱小豪也很尽心为儿子的事业出力，亲自捧儿子做男主角。\"},{\"type\":\"text\",\"value\":\"第六位，吕颂贤\"},{\"type\":\"text\",\"value\":\"港圈巅峰时期捧红了不少男神，不过就不是每个人都能成为刘德华和古天乐这样的常青艺人，比如至今仍然被封为最经典“令狐冲”的扮演者吕颂贤，曾经风靡一时的他，如今在上海街头，却没几个人认识他。\"},{\"type\":\"text\",\"value\":\"之前吕颂贤让助理拿着自己的照片在上海街头问路人，最后只有一位小哥哥认出是吕颂贤。吕颂贤当年在事业上升期离开TVB，有传闻说他为了照顾身体不好的妻子，也有传闻说他和TVB闹不和。\"},{\"type\":\"text\",\"value\":\"来内地定居几年，吕颂贤一开始并没有把重心放在内地娱乐行业，所以他现在变得“无人识”也可以理解，不过他在内地开了几间“素食餐厅”，算是做了小生意。\"},{\"type\":\"text\",\"value\":\"最近他还在筹谋开直播公司，不过以他现在的人气，之前在直播间卖睡衣时人气一般，远远比不上其他明星的人气。\"},{\"type\":\"text\",\"value\":\"第七位，范植伟\"},{\"type\":\"text\",\"value\":\"19岁就出道，凭借《咸豆浆》，《地下铁》等作品走红，还获得影帝称号的范植伟曾经是台湾当红小生，因为长相出众还有“小梁朝伟”的称号，可见当时外界对他的追捧程度。\"},{\"type\":\"text\",\"value\":\"可能是因为年少成名，范植伟在感情生活上失了方寸，不但公开大曝前女友王心凌的隐私，还和其他女艺人传绯闻，让他的名声受损。\"},{\"type\":\"text\",\"value\":\"之后范植伟来内地发展，但成绩平平。再次回到台湾时，甚至要靠母亲的20万存款过日子。如今40岁的范植伟可以说已经是“过气艺人”，外型上也和年轻时没得比，身材发福皮肤松弛，五官变得普通气质平平，留着邋遢的长发。\"},{\"type\":\"text\",\"value\":\"但心高气傲的范植伟，还说自己人气一直没有减弱。\"},{\"type\":\"text\",\"value\":\"不过他也不得不接受现实，为了生计，他一度去一家火锅店工作，拿着每个月近7千块工资，至少足够基本生活费用了。而近日，因为他父亲生病，为了有时间照顾父亲，范植伟还从火锅店辞职，去便利店兼职。\"},{\"type\":\"text\",\"value\":\"第八位，朱孝天\"},{\"type\":\"text\",\"value\":\"曾经红极一时的“F4”组合，即使过了近20年仍然很有人气，比如言承旭近期参加《披荆斩棘的哥哥》，就是里面的人气担当。但同为F4成员之一的朱孝天就没有那么幸运了。\"},{\"type\":\"text\",\"value\":\"据朱孝天前段时间透露，因为没有人脉和资源，他已经接近一年时间没有工作和收入，每天在家里打游戏。朱孝天也自嘲自己是F4里面混得最差的，他还说自己之前也没有赚到很多钱，因为大部分钱都给了公司。\"},{\"type\":\"text\",\"value\":\"不过幸好他和内地妻子韩雯雯感情很好，朱孝天还说妻子曾经安慰他，找不到工作可以养他。朱孝天因为遗传病的原因，和妻子结婚5年一直没有生孩子。\"},{\"type\":\"text\",\"value\":\"而近期，朱孝天还打算让妻子跟着自己留在台湾发展，让妻子进入台湾娱乐圈，另外，他也准备和妻子一起拍短视频。\"},{\"type\":\"text\",\"value\":\"第九位，叶蕴仪\"},{\"type\":\"text\",\"value\":\"当红明星从“风光”走向“落魄”，有人是“自毁前程”，有人是“嫁错郎”。叶蕴仪曾经是港圈的“玉女”和“甜妹”代表人物，20岁已经是当红女星。可惜的是，她在22岁就嫁给富商，演艺事业也因此搁置。\"},{\"type\":\"text\",\"value\":\"叶蕴仪的富商丈夫婚后露出了“风流”本性，最后还是离婚收场。离婚后，叶蕴仪得到了一对子女的抚养权，男方被判定需要一次性付给叶蕴仪800万和每个月5000元抚养费。\"},{\"type\":\"text\",\"value\":\"但叶蕴仪前夫却是实实在在的“渣男”，不但离婚后公开曝叶蕴仪隐私，害她名声大跌，之后还破产了，叶蕴仪也得不到任何抚养费，只能靠自己养活一对子女。\"},{\"type\":\"text\",\"value\":\"叶蕴仪最落魄的时候，身材走形样子憔悴，一夜之间从女神变成“大妈”，一度来内地做“售楼小姐”。\"},{\"type\":\"text\",\"value\":\"好在近期，叶蕴仪已经成功减肥32斤，状态大好，还复出拍戏，还加入了直播行业。不过为了两个儿女，叶蕴仪一直没有再婚。\"},{\"type\":\"text\",\"value\":\"第十位，李绮红\"},{\"type\":\"text\",\"value\":\"令人惋惜的港圈女神有不少，李绮红也是其中一个。李绮红是94年的港姐季军，代表作有《神雕侠侣》的“郭襄”和《绝代双骄》里的“小辣椒”，她的长相很有特色，眼见在娱乐圈前途一片光明。\"},{\"type\":\"text\",\"value\":\"无奈的是，2002年，处于事业上升期的李绮红因病淡出娱乐圈，和外国男友结婚生子，定居国外多年。\"},{\"type\":\"text\",\"value\":\"在国外几年间，李绮红在丈夫照顾下已经恢复健康，但身材发福不少，皮肤松弛蜡黄，变得星味全无。她在国外已经考取了护士证，一度在国外当护士。\"},{\"type\":\"text\",\"value\":\"08年之后她回香港复出拍戏，但都是演一些小配角，近几年更加是“消失”在娱乐圈。李绮红不久前还在社交平台直言，自己并没有退出娱乐圈，只是一直接不到戏，还希望能来内地发展。\"},{\"type\":\"text\",\"value\":\"看了这10位明星的经历，他们从风光都落魄各有各的原因，有人让人觉得可惜，有人却是自己造成，也让人感觉到，明星也有自己的不容易。不管以前怎么样，希望他们以后能努力过好自己的生活就好了。\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:45','2021-11-26 15:41:45',9,'2021-11-26 15:41:45','阿里云调用失败，转人工审核',1464138457679945729,'',1),(6286,1102,'孙莉17岁就被黄磊“骗到手” 学生时代照片曝光','[{\"type\":\"text\",\"value\":\"大家对黄磊都不陌生了，作为《极限挑战》中的“神算子”，黄磊靠着超高的智商赢得了不少观众的喜爱。然而，比起被他智商碾压，他和孙莉的爱情故事，更是喂了观众一嘴狗粮。\"},{\"type\":\"text\",\"value\":\"如果说到娱乐圈的模范夫妻，绝对少不了黄磊和孙莉。很多人不知道的是，这对夫妻还是师生恋。1995年，在北京电影学院任教的黄磊，遇见了正在新生入学考试的孙莉。黄磊对她一见钟情，而当时孙莉只有17岁。\"},{\"type\":\"text\",\"value\":\"很多人不明白，为什么黄磊这么猴急，要对未成年女学生“下手”。不过，现在已经生了两个孩子的孙莉身材依然这么好，当年应该更是惊艳。\"},{\"type\":\"text\",\"value\":\"其实，孙莉结婚后就逐渐退出演艺圈了。在她留下的为数不多的影视作品中，我们能看到，孙莉属于第二眼美女。初看并不惊艳，再看惊为天人。不得不说，黄磊确实有眼光。\"},{\"type\":\"text\",\"value\":\"大家对此有什么看法呢？欢迎留言讨论~\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:46','2021-11-26 15:41:46',9,'2021-11-26 15:41:46','阿里云调用失败，转人工审核',1464138457835134977,'',1),(6287,1102,'林依晨生女后首次公开亮相 一袭长裙笑容甜','[{\"type\":\"text\",\"value\":\"测试文章内容\"}]',0,6,'爬虫','2021-11-26 15:41:47','2021-11-26 15:41:47',9,'2021-11-26 15:41:47','阿里云调用失败，转人工审核',1464138457969352706,'',1),(6288,1102,'2021年TVB颁奖礼提名名单公布 黎耀祥陈豪争视帝','[{\"type\":\"text\",\"value\":\"网易娱乐11月26日报道 TVB万千星辉颁奖礼2021提名名单公布。\"},{\"type\":\"text\",\"value\":\"陈豪、郭晋安、欧阳震华、马德钟、马国明、袁伟豪、郑嘉颖、黎耀祥等争视帝，滕丽名、宣萱、王君馨、万绮雯、惠英红、黄智雯、黄翠如、钟嘉欣、米雪等争视后，《拳王》《星空下的仁医》《陀枪师姐2021》《爱‧回家之开心速递》等竞争最佳剧集。\"},{\"type\":\"text\",\"value\":\"你看好谁呢？\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：蒋紫莹_NBJS16943)\"}]',0,6,'爬虫','2021-11-26 15:41:48','2021-11-26 15:41:48',9,'2021-11-26 15:41:48','阿里云调用失败，转人工审核',1464138458111959042,'',1),(6289,1102,'谢娜向包文婧借3万欧元还3万人民币 还嫌她太计较','[{\"type\":\"text\",\"value\":\"一次，谢娜在国外旅游时，向包文婧借了3万块澳元，后来，谢娜只还了3万人民币，包文婧就提醒她，再补12万。\"},{\"type\":\"text\",\"value\":\"谢娜却不高兴地说：“太计较了，要是我都不好意思提醒，就说已经收到了！”\"},{\"type\":\"text\",\"value\":\"要说娱乐圈的“抠门”人设，谢娜和包文婧妥妥榜上有名。谢娜，自称是抠门界的师父，还收了杨迪和魏大勋两个徒弟。\"},{\"type\":\"text\",\"value\":\"一次，沈腾开生日宴会，喝酒喝多了，让谢娜去结账。\"},{\"type\":\"text\",\"value\":\"谢娜当时也喝得醉熏熏的，迷迷糊糊就听了沈腾的话，乖乖地付了钱。\"},{\"type\":\"text\",\"value\":\"第二天早上六点多，谢娜醒来，一看短信才发现，昨晚居然帮沈腾垫钱。\"},{\"type\":\"text\",\"value\":\"于是，她急忙给沈腾夺命连环扣，让他还钱。\"},{\"type\":\"text\",\"value\":\"谢娜扰沈腾清梦，沈腾对她置之不理。后来，索性关机，图个清净。\"},{\"type\":\"text\",\"value\":\"没想到，这之后，谢娜每次看到沈腾，第一句话就是：“还钱。”\"},{\"type\":\"text\",\"value\":\"也许是沈腾觉得谢娜这朋友没意思，故意不还。\"},{\"type\":\"text\",\"value\":\"最终，谢娜使出绝招：她邀请沈腾上她的一档节目，最终用沈腾的出场费，抵扣沈腾欠她的钱。\"},{\"type\":\"text\",\"value\":\"后来，魏大勋也向谢娜借钱，之后也忘了还钱。\"},{\"type\":\"text\",\"value\":\"谢娜干脆就在微博上，向魏大勋“催债”。\"},{\"type\":\"text\",\"value\":\"不出她所料，很快，钱就收回来了。\"},{\"type\":\"text\",\"value\":\"谢娜也因此感叹：“网友的力量是巨大的！”\"},{\"type\":\"text\",\"value\":\"谢娜有如此“辉煌”的过去，妥妥做实了“抠门”的人设。\"},{\"type\":\"text\",\"value\":\"但是，比起谢娜，包文婧的抠门程度，也绝对有的一拼。\"},{\"type\":\"text\",\"value\":\"包文婧结婚时，听到包贝尔说结婚戒指是租的，怕花钱赶紧要他去退掉。\"},{\"type\":\"text\",\"value\":\"包文婧生孩子的时候，听到包贝尔钱包找不到了，不顾做月子马上要跑出医院寻找。\"},{\"type\":\"text\",\"value\":\"她不仅对自己抠门，也对朋友也抠门。\"},{\"type\":\"text\",\"value\":\"她说自己处了14年的闺蜜，自己一次都没有请她吃过饭，都是吃别人的。\"},{\"type\":\"text\",\"value\":\"不仅如此，平时她过生日，即便朋友给她送了价格不菲的大牌，她回礼时，最多只会回价格不超过100块的头绳。\"},{\"type\":\"text\",\"value\":\"包文婧的抠门程度，也是没谁了。\"},{\"type\":\"text\",\"value\":\"那么，不少人就好奇，谢娜和包文婧这两个抠门大神，究竟谁更抠门？\"},{\"type\":\"text\",\"value\":\"综艺节目《妻子的浪漫旅行》，给出了答案。\"},{\"type\":\"text\",\"value\":\"当时，妻子们到澳洲旅行。\"},{\"type\":\"text\",\"value\":\"谢娜，看中了一个价值3万澳元的编织包。\"},{\"type\":\"text\",\"value\":\"不过，她带的钱不多，在一顿买买买后，手头根本没那么多钱。\"},{\"type\":\"text\",\"value\":\"于是，她借了一圈，发现只有舍不得花钱的包文婧，手里还有钱，便和她借了3万澳元。\"},{\"type\":\"text\",\"value\":\"后来，回到国内后，谢娜似乎忘了这件事，好长一段时间都没把钱还给包文婧。\"},{\"type\":\"text\",\"value\":\"包文婧也不大好意思和谢娜提，期待她能够自己想起来。\"},{\"type\":\"text\",\"value\":\"后来，谢娜和包文婧的老公包贝尔，一起参加一档节目，她也终于想起了向包文婧借钱的事。\"},{\"type\":\"text\",\"value\":\"不久后，谢娜就将钱还给了包文婧。\"},{\"type\":\"text\",\"value\":\"然而，包文婧一看，这不对！\"},{\"type\":\"text\",\"value\":\"谢娜向包文婧借的是澳元，但是还的却是人民币，这中间整整差了12万！\"},{\"type\":\"text\",\"value\":\"抠门的包文婧，怎么可能放过这12万呢！\"},{\"type\":\"text\",\"value\":\"最终，她还是给谢娜打电话：\"},{\"type\":\"text\",\"value\":\"“娜娜，你还的钱我收到了。”\"},{\"type\":\"text\",\"value\":\"“不过，这个数字不对哈，我借给你的是3万澳元，相当于15万人民币，你少还了12万！”\"},{\"type\":\"text\",\"value\":\"后来，谢娜还了，不过她却不忘在节目中吐槽：\"},{\"type\":\"text\",\"value\":\"“包文婧真是太计较了，要是我，都不好意思要了，就说钱已经收到了！”\"},{\"type\":\"text\",\"value\":\"想起之前沈腾没还谢娜钱，谢娜就契而不舍地要回来！\"},{\"type\":\"text\",\"value\":\"谢娜欠包文婧钱，包文婧要回来，谢娜却嫌弃她太计较，也是没谁了。\"},{\"type\":\"text\",\"value\":\"随着这件事的落幕，谢娜和包文婧两人，究竟谁比较抠门，答案也揭晓。\"},{\"type\":\"text\",\"value\":\"还是谢娜大获全胜啊！\"},{\"type\":\"text\",\"value\":\"@小小美的异想世界 ：谢娜成名早，赚的也多，她的抠门，着实让人难以理解。\"},{\"type\":\"text\",\"value\":\"不过，包文婧的抠门，似乎显得比较亲民。\"},{\"type\":\"text\",\"value\":\"包文婧从小出生于一个穷苦家庭，长大后，又陪着没有成名、一穷二白的包贝尔，在出租房里过了好几年的苦日子。\"},{\"type\":\"text\",\"value\":\"所以，对她来说，贫穷的记忆，已经深深刻在她的脑海中。\"},{\"type\":\"text\",\"value\":\"即便后来有钱了，她还是恨不得把一块钱掰成两块钱用，能不花钱的就不花钱。\"},{\"type\":\"text\",\"value\":\"所以，在《妻子的浪漫旅行》中，其他女嘉宾都带名牌配饰，唯独包文婧身上的配饰，看着很廉价。\"},{\"type\":\"text\",\"value\":\"所以，即便现在有条件了，她甚至给自己买稍微贵一点的鞋子，都不舍得。\"},{\"type\":\"text\",\"value\":\"她不舍得给自己花钱，也不舍得给朋友花钱，所以相处14年的朋友，都没吃过她一段饭。\"},{\"type\":\"text\",\"value\":\"不过，她却会在朋友不开心的时候，第一时间到她的身旁，安慰她，用陪伴代替金钱的付出。\"},{\"type\":\"text\",\"value\":\"这样的抠门，其实是经历带来的，实属无奈。\"},{\"type\":\"text\",\"value\":\"不过，即便是这样抠门的包文婧，对于自己很爱的人，她也是很大方。\"},{\"type\":\"text\",\"value\":\"在《妻子的浪漫旅行》中，包文婧舍不得给自己买一双鞋，却给家里的老人买了好几副老花镜。\"},{\"type\":\"text\",\"value\":\"对于孩子，她也是很舍得。\"},{\"type\":\"text\",\"value\":\"在孩子6岁生日的时候，她给孩子举办了一场盛大的生日宴会。\"},{\"type\":\"text\",\"value\":\"看着简直就像公司的年会，就为了孩子开心。\"},{\"type\":\"text\",\"value\":\"感觉，谢娜的抠门更接近精明，包文婧的抠门，似乎更有人情味。 图片源自网络，侵删。\"},{\"type\":\"text\",\"value\":\"[呲牙]小互动：友友身边有抠门的朋友么？有比谢娜和包文婧抠门的么？欢迎留言[心]\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:49','2021-11-26 15:41:49',2,'2021-11-26 15:41:49','文本中有违规内容，审核失败',NULL,'',1),(6290,1102,'大S离婚后首露脸又瘦又白 大方搂男闺蜜心情好','[{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"11月25号，知名造型师小P老师分享了一组聚会照，配文：好朋友大聚会，时间都去哪了，一转眼大家都认识30年了！小P老师艾特了大S，小S、范晓萱以及范玮琪，大家都知道大小S姐妹跟范晓萱、范玮琪是多年好友，妥妥的姐妹团，没想到小P老师也跟大小S姐妹团成员相识30年了。\"},{\"type\":\"text\",\"value\":\"小P老师先P出自己跟大小S姐妹俩的合照，关注娱乐圈的小伙伴们应该知道，大S前两天刚跟汪小菲官宣了离婚消息，这是大S离婚后首次露脸，可见大S不受离婚影响，跟好朋友们聚餐超开心。\"},{\"type\":\"text\",\"value\":\"只见大S穿着波点衬衣，泡泡袖设计增加了几分少女的甜美感，大S化了淡妆，戴了耳环，还做了精致的美甲，打扮得很贵气，大S今年鲜少露脸，前几天小S说姐姐瘦了不少，美回“杉菜”时期，看了照片，确实如小S所说，相比去年，大S明显消瘦不少，下颚线也明朗起来，有少女“杉菜”的味道了，大S皮肤状态也很好，40多岁，皮肤依旧白皙紧致，状态好好。\"},{\"type\":\"text\",\"value\":\"搂着男闺蜜“小P老师”拍照的大S心情看起来很不错呢。\"},{\"type\":\"text\",\"value\":\"相比姐姐，小S参加派对的着装比较休闲，一身绿色运动服，青春有活力，小S跟小P老师的拍照pose要霸气许多，胳膊搭在对方肩膀上，整个人超酷，范晓萱则比较搞怪。\"},{\"type\":\"text\",\"value\":\"大合照中，大S靠在姐姐肩头笑嫣如花，整个人开心得不得了，虽说刚经历的离婚一事，但是有家人、朋友的陪伴，大S也没有沉浸在离婚的情绪中，俨然已经对这件事情释然了，已经开启美好生活的新篇章。\"},{\"type\":\"text\",\"value\":\"网友们也在评论区夸赞大S果然又变回“杉菜”了，又瘦又美太酷了，大家30年的友情也很让人羡慕。\"},{\"type\":\"text\",\"value\":\"看到大S状态这么好，粉丝也都很欣慰，而汪小菲也不受离婚影响，离婚后首次现身自家店所在的商场，看起来还胖了一些，汪小菲现阶段显然是把精力都放在事业上了，祝两人都能越来越幸福吧。\"},{\"type\":\"text\",\"value\":\"【注：本文为作者原创，欢迎转发文章，订阅关注，未经授权，转载必究！图片来源于网络，如涉及版权请联系删除】\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"},{\"type\":\"text\",\"value\":\"(责任编辑：彭欣怡_NBJS16638)\"}]',0,6,'爬虫','2021-11-26 15:41:50','2021-11-26 15:41:50',2,'2021-11-26 15:41:50','文本内容包含自管理的敏感词: {66=1}',NULL,'',1),(6291,1102,'高圆圆车展旧照火了 焦点却在主持人的身上','[{\"type\":\"text\",\"value\":\"相信很多人都知道，前不久，高圆圆亮相车展被指排场太大，一时间，网上铺天盖地都是关于高圆圆亮相车展的帖子，在这些高圆圆亮相车展的帖子中，除了关于高圆圆前不久亮相车展的帖子以外，还有高圆圆早期亮相车展的帖子，一组高圆圆亮相车展的旧照曝光，引起了不少网友的关注和热议。\"},{\"type\":\"text\",\"value\":\"11月25日，有一位娱乐博主在网上发布了一篇关于高圆圆亮相车展的帖子，晒出了一组高圆圆亮相车展的旧照，并表示“高圆圆参加车展的旧照，穿青色一字肩扎丸子头，造型清爽，尽显好身材，看起来气质有点轻熟，不得不说，还是以前的高圆圆更迷人！”短短几个小时，网友浏览次数就超过了600万次，很显然，这一组高圆圆亮相车展的旧照火了。\"},{\"type\":\"text\",\"value\":\"从该娱乐博主晒出的这一组高圆圆亮相车展的旧照来看，这应该是高圆圆3年前亮相车展的照片，时隔3年，这一组高圆圆的照片再次被翻出，依然引起了几百万网友的注意，由此可见，这一组高圆圆亮相车展的旧照应该很有看点，同时也证明了关注高圆圆的人很多。\"},{\"type\":\"text\",\"value\":\"高圆圆亮相车展的造型就是一大看点，高圆圆穿着一件青色的一字肩长裙，这件长裙的剪裁格外地修身，很好地勾勒出了高圆圆的身材曲线，高圆圆侧身对着镜头时，身材曲线很美，甚至可以用凹凸有致来形容高圆圆的身材，丸子头也很加分，提升了高圆圆的甜美感，妩媚与甜美相互交织，显得高圆圆格外的轻熟。\"},{\"type\":\"text\",\"value\":\"本以为网友们的关注点会在高圆圆的身上，结果大部分网友的关注点都在主持人的身上，从网友们在评论区留言的内容来看，不仅有网友认为主持人的身材很好，而且还有网友认为主持人比高圆圆更美，甚至还有网友表示“讲道理，我还以为高圆圆是主持人”。\"},{\"type\":\"text\",\"value\":\"在这一组高圆圆亮相车展的旧照中，有一张是高圆圆和主持人同框的照片，从这一张照片来看，主持人穿着一件拼色长裙，身材和气质都很不错，不过，我并不太同意主持人比高圆圆更美的说法，我个人觉得，高圆圆和主持人都很美，只是两人的美感不同而已，你们觉得呢？\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:51','2021-11-26 15:41:51',9,'2021-11-26 15:41:51','阿里云调用失败，转人工审核',1464138458262953986,'',1),(6292,1102,'那些削骨的女星脸还好吗？后遗症一大堆！','[{\"type\":\"text\",\"value\":\"所谓爱美之心人皆有之，这句话在女明星这里，已经发挥到极致了。为了让自己的脸蛋更漂亮，不少女明星都踏上了动脸的道路，细数女星做的动脸项目，其中最为火爆的就是削骨了。\"},{\"type\":\"text\",\"value\":\"早些年，流行一刀切式的削骨，这种方式简单粗暴，变成尖脸不是梦，但后遗症也是比较明显的，不少女明星一刀削骨后的后遗症，日渐暴露。\"},{\"type\":\"text\",\"value\":\"第一个后遗症就是脸型容易变得奇怪，从下面的一刀切削骨示意图就可以看出，一刀切削骨后，脸是变窄了，但又会形成一个新的棱角。\"},{\"type\":\"text\",\"value\":\"1.杨幂\"},{\"type\":\"text\",\"value\":\"杨幂就曾因为一刀削骨，脸型变得奇怪。\"},{\"type\":\"text\",\"value\":\"她的脸型变化，大家是有目共睹的。杨幂的原生脸型，是最标准的大方脸，她20岁的时候，在《神雕侠侣》出演“郭襄”崭露头角，当时杨幂虽然灵气十足，但脸型的缺陷也很明显，脸方，下颌骨靠后。\"},{\"type\":\"text\",\"value\":\"这个问题想必杨幂和团队都意识到了，后面几年杨幂再出现，脸型就已经变化了，她的脸明显变窄了，原本靠后的下颌骨消失。\"},{\"type\":\"text\",\"value\":\"这时候的杨幂，脸型精致了不少，上镜更加好看了。\"},{\"type\":\"text\",\"value\":\"杨幂脸型变窄变精致的同时，也有后遗症出来，那就是脸型变得有些奇怪了，直接从方脸变成了多边形。\"},{\"type\":\"text\",\"value\":\"随着时间的流逝，杨幂的多边形脸更加明显，脸型越来越像多边形，一刀削骨后，多出来的那个棱角也越加明显了。\"},{\"type\":\"text\",\"value\":\"在这个时期，杨幂的脸型是相当奇怪的，还被网友调侃像极了“嫩牛五方”。\"},{\"type\":\"text\",\"value\":\"从网上网友做出的对比图来看，杨幂当时的脸型确实是和嫩牛五方一模一样，都是上面尖，下面方，左右还有棱角。\"},{\"type\":\"text\",\"value\":\"这个时期杨幂的脸虽然比原生方脸时期，看起来窄了小了许多，但脸型确实不敢细看。\"},{\"type\":\"text\",\"value\":\"嫩牛五方脸持续了一段时间之后，杨幂的脸又做了调整，变成了小v脸。这时候，一刀削骨之后脸型变成多边形的情况减弱了，多出来的棱角也不那么明显了。\"},{\"type\":\"text\",\"value\":\"不过近些年，杨幂还是时不时的就被爆出脸垮，从一些生图看，杨幂的脸现在有点挂不住肉的感觉。\"},{\"type\":\"text\",\"value\":\"尤其是做一些大幅度表情的时候，感觉脸上的肉已经完全挂不住了，看起来十分夸张。\"},{\"type\":\"text\",\"value\":\"2.鞠婧祎\"},{\"type\":\"text\",\"value\":\"号称四千年美女的鞠婧祎，如今的脸蛋越发精致，但她早期的脸蛋，可没有现在这么精致。\"},{\"type\":\"text\",\"value\":\"从早期鞠婧祎参加选秀节目的照片，她的原生脸型比较大，而且宽，也属于方脸。\"},{\"type\":\"text\",\"value\":\"可如今鞠婧祎的脸却完全变成了小V脸，精致小巧，其实这期间，鞠婧祎经历了3个阶段，现在的脸型是3.0阶段。\"},{\"type\":\"text\",\"value\":\"在2.0阶段的时候，鞠婧祎的脸也出现了杨幂同款后遗症，那时候她的脸型也有一种多边形的感觉，一刀削骨之后，多出来的棱角明显，而且脸部侧面还有点挂不住肉。\"},{\"type\":\"text\",\"value\":\"到了3.0版本之后，鞠婧祎的脸更窄了，但还是有些多边形的感觉，脸部两侧凹陷，脸部形状整体看起来不太自然。\"},{\"type\":\"text\",\"value\":\"但她的脸还是不能做大幅度的表情，一大笑脸就有崩塌的感觉，虽然她用手挡住了脸，但从侧面还是能看到，她的脸是歪的，侧面的肉一条一条的，根本挂不住。\"},{\"type\":\"text\",\"value\":\"在综艺节目中，说话的时候，鞠婧祎的脸也是明显歪了，一边脸被头发遮住看不见，另一边脸没来得及遮住，直接歪成了鞋拔子，看起来相当夸张。\"},{\"type\":\"text\",\"value\":\"也正是因为鞠婧祎现在的脸生图，总是容易崩，所以网友调侃鞠婧祎是“四千刀”、“随时需要返厂重修”。\"},{\"type\":\"text\",\"value\":\"3.尚雯婕\"},{\"type\":\"text\",\"value\":\"尚雯婕参加超女的时候，脸还比较原生态，脸方且大，看起来有点男生相。超女之后，尚雯婕开始大变脸，以往的大方脸，直接变成了超级小V脸。\"},{\"type\":\"text\",\"value\":\"虽然当时尚雯婕声称是减肥的原因，但网友的眼睛不瞎，懂得都懂。\"},{\"type\":\"text\",\"value\":\"先不谈尚雯婕其他部位的变化，单是脸型的变化就很大了，而且能看出来，她在脸型上动得功夫不少。虽说她的脸是变小了，但后期的后遗症也是层出不穷。\"},{\"type\":\"text\",\"value\":\"她的脸挂不住肉，有种松垮的感觉。\"},{\"type\":\"text\",\"value\":\"尚雯婕动脸的时候，应该是下手太重了，她的下巴看起来相当的尖，一说话，下巴的假体都有种呼之欲出的感觉，看起来有几分可怖。\"},{\"type\":\"text\",\"value\":\"4.秦岚\"},{\"type\":\"text\",\"value\":\"秦岚早期的脸也是偏方的，虽然她的脸型没有特别宽，但方方的，下颌骨也靠后，从她在《还珠格格》中的剧照，就可以看出来。\"},{\"type\":\"text\",\"value\":\"但渐渐的秦岚的脸就是变尖了，而且是越来越尖。对比她“知画”时期的照片，和现在的照片，脸型差别不要太大。\"},{\"type\":\"text\",\"value\":\"后遗症也随之而来了，现在秦岚一说话，下巴就有种要原地起飞的感觉，脸上的肉下垂松弛，完全挂不住。\"},{\"type\":\"text\",\"value\":\"看正脸还好，一看侧脸，后遗症完全藏不住。\"},{\"type\":\"text\",\"value\":\"5.孙菲菲\"},{\"type\":\"text\",\"value\":\"孙菲菲早期的颜值就非常高，是出了名的古装美人，不仅五官精致，模样姣好，气质也相当出众。\"},{\"type\":\"text\",\"value\":\"其实当时她的脸型就已经很漂亮了，不方也不大，只是稍微有点肉感，不算是硬伤。\"},{\"type\":\"text\",\"value\":\"看她的自拍照，明显能看出她的脸切得挺狠的，下巴尖得能戳死人，透着一股说不出的僵硬。孙菲菲这个脸已经属于动脸的反面典型了，本来是个女神，非要搞成蛇精脸。\"},{\"type\":\"text\",\"value\":\"明明已经很漂亮了，但孙菲菲还是去动了脸。她动脸后，直接从一个气质美人，变成了网红脸，她的脸变小了，下巴也变尖了，网友称她整容户变成了“低配杨幂既视感”。\"},{\"type\":\"text\",\"value\":\"美女为什么要这么“想不开”啊？\"},{\"type\":\"text\",\"value\":\"6.张萌\"},{\"type\":\"text\",\"value\":\"张萌早年脸上有婴儿肥，脸型圆圆的，下巴短，不算精致，但胶原蛋白满满，感觉比较有少女感。\"},{\"type\":\"text\",\"value\":\"到了《神话》时期，张萌的圆脸就已经变尖了，同时段参加活动时，张萌的脸完全变成了小尖脸。这个时候，应该算是张萌颜值的高光时期，动脸的痕迹还不算重 。\"},{\"type\":\"text\",\"value\":\"但随着时间的推移，张萌的脸变成一眼就能看出来动脸痕迹的程度，脸上全都是后遗症。\"},{\"type\":\"text\",\"value\":\"先不说别的部位，但是下巴就尖得下人，脸上的也挂不住肉了。\"},{\"type\":\"text\",\"value\":\"一做起大的表情，就有种随时要崩塌的感觉，脸型越发奇怪呢，脸蛋有几分吓人，怎么看怎么不对劲，看起来还有些狰狞。\"},{\"type\":\"text\",\"value\":\"这些女明星为了变成小尖脸，选择了简单粗暴的方式，脸是变小变尖了，但随之而来的还有后遗症。\"},{\"type\":\"text\",\"value\":\"其实并不是只有小尖脸才好看，例如舒淇、巩俐等女星，都是方脸，她们没有去削骨，却依旧是女神，不仅美出了自己的特色，也没有所谓的后遗症，多年来一直保持着好状态。\"},{\"type\":\"text\",\"value\":\"所以说啊，削骨需谨慎，而且女明星们也要多提高美商，美出自己的特色，清一色的锥子脸，有什么看头呢？\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:53','2021-11-26 15:41:53',9,'2021-11-26 15:41:53','阿里云调用失败，转人工审核',1464138458397171714,'',1),(6293,1102,'欧阳娜娜分享美照 穿抹胸裙戴皇冠十分甜美','[{\"type\":\"text\",\"value\":\"测试文章内容\"}]',0,6,'爬虫','2021-11-26 15:41:54','2021-11-26 15:41:54',9,'2021-11-26 15:41:54','阿里云调用失败，转人工审核',1464138458523000834,'',1),(6294,1102,'贾静雯早期写真被翻出！穿红裙坐沙发上身材傲人','[{\"type\":\"text\",\"value\":\"11月25日，有一位娱乐博主在网上分享了一组贾静雯早期的写真作品，并配文“贾静雯早期的写真作品，穿红色连衣裙，搭配黑色高跟鞋，造型洋气，坐在沙发上摆拍，坐姿优雅，气质好，身材棒，不得不说，早期的贾静雯确实迷人！”看得出来，该娱乐博主对这一组贾静雯早期写真作品的评价挺高。\"},{\"type\":\"text\",\"value\":\"这一组贾静雯早期的写真被翻出2个小时以后，浏览量就超过了200万，网友点赞次数高达1万次，在我的印象中，如今的贾静雯已经不怎么活跃了啊，貌似她已经很久没有新的影视上线了，也很久没有出席活动了，没想到她居然还有这么的人气，也许这就是真正的明星吧。\"},{\"type\":\"text\",\"value\":\"从该娱乐博主晒出的这一组贾静雯早期的写真作品来看，这一组贾静雯的写真作品确实有再次翻红的道理，就拿贾静雯的穿衣搭配来说，贾静雯穿着一件红色的吊带裙，搭配了一双黑色的高跟鞋，这一红一黑的搭配，显得她的气质格外的妩媚，另外，这件红色吊带裙还采用了低领设计，发挥出了贾静雯的身材优势，大大地提升了贾静雯的女人味。\"},{\"type\":\"text\",\"value\":\"虽然贾静雯的穿衣搭配很显身材，但是她的气质依然显得格外的儒雅，这一点得力于贾静雯的坐姿，在这一组贾静雯早期的写真作品中，有一张是贾静雯坐在沙发上摆拍的照片，贾静雯坐在沙发摆拍时，她的坐姿格外的优雅，不仅提升了她的高级感，而且还显得她很大气。\"},{\"type\":\"text\",\"value\":\"从网友们对这一组贾静雯早期写真作品的评价来看，不仅有网友认为贾静雯的身材很好，而且还有网友认为贾静雯很迷人，不过，也有网友认为造型太显身材了，降低了贾静雯的高级感，显得贾静雯很风尘，看得出来，网友们对贾静雯的评价存在严重的两极分化。\"},{\"type\":\"text\",\"value\":\"不可否认，贾静雯的造型确实挺显身材，不过，我并不赞同贾静雯风尘味很重的说法，虽然有一些女明星秀身材的时候风尘味很重，但是也有一些女明星秀身材时高贵感很足啊，我个人觉得，贾静雯就是属于秀身材高级感很足的女明星，你们觉得呢？\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:55','2021-11-26 15:41:55',9,'2021-11-26 15:41:55','阿里云调用失败，转人工审核',1464138458623664130,'',1),(6295,1102,'《快乐大本营》回归录制？现场照曝光引欢呼','[{\"type\":\"text\",\"value\":\"自清朗行动开展以来，《快乐大本营》为了响应国家号召，强力进行停播改版。到目前为止，《快乐大本营》已停播三周，广大粉丝甚是想念。那么《快乐大本营》什么时候才能回归呢？现在好消息来了。\"},{\"type\":\"text\",\"value\":\"近日，有网友晒出《快乐大本营》在某图书馆拍摄的场地路透。从照片中我们可以看到，某图书馆的场馆内立着牌子，上面写着“本区域已被活动（快乐大本营）拍摄预留，给您带来不便，敬请谅解”，时间是：2021年11月25日，9：00-15:00。而相关的区域已被围起来。看来，《快乐大本营》已开始恢复录制。\"},{\"type\":\"text\",\"value\":\"此消息曝光后，网友激动不已，纷纷发文感谢博主。大家期盼的心，终于看到了希望。\"},{\"type\":\"text\",\"value\":\"《快乐大本营》是一档嘉宾访谈游戏秀节目，自1997年播出至今，已有24个年头，属于综艺节目的常青树。也是电视台的台柱子，目前，还没有哪个综艺可以比拟。节目自开播以来，深受观众喜爱，每一期节目都能带给大家很多欢乐。\"},{\"type\":\"text\",\"value\":\"同时，节目中的五位主持人，也变成了标配。虽然何炅的台词更多，但是其他人也都很配合。即使其他人受到了众多诟病，但是他们缺一不可。何炅、杜海涛、谢娜、吴昕、李维嘉已经融为了一体，都很棒。\"},{\"type\":\"text\",\"value\":\"其实，五位主持人每一个人都可以独当一面。只不过何炅是主持界的老大，其他主持人只能让位。\"},{\"type\":\"text\",\"value\":\"随着节目的不断壮大，以及网络形式的不断发展。《快乐大本营》发生了一些微妙的变化。以前是谁上《快乐大本营》谁火，现在是谁火谁上，节目已经失去了当年的影响力。被《快乐大本营》捧红的艺人有很多，比如：魏大勋、熊梓淇、杨迪、张杰、李浩菲等等。\"},{\"type\":\"text\",\"value\":\"从目前爆料的改版方案中我们可以看到，快乐大本营将会去娱乐化，集中力量创新，加入更多的青春元素，更加聚焦传统文化、文化交流、科技科教等主题。\"},{\"type\":\"text\",\"value\":\"同时增加了人工智能主持人，让科技引领潮流。这样一来五位主持人，将变成六位，节目形式更加的完善。\"},{\"type\":\"text\",\"value\":\"作为一档全民影响力很广的节目，确实应该改版。他需要有社会担当，去帮助和引导年轻人去学习更多的文化和知识，而不是一味地追星和玩乐。我相信改版后的《快乐大本营》会让大家满意，实现他应有的价值！\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:56','2021-11-26 15:41:56',9,'2021-11-26 15:41:56','阿里云调用失败，转人工审核',1464138458783047681,'',1),(6296,1102,'71岁谭咏麟睡小48岁女粉？女粉男友再晒脱裤照','[{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"近日，一名男网友爆料谭咏麟睡了女粉丝，并且这名女粉丝就是自己的女朋友，让自己十分难受。这名男粉丝发长文痛斥谭咏麟人前一套人后一套，不配德高望重的谭校长称号，一时间也是引起网友们的围观和热议。\"},{\"type\":\"text\",\"value\":\"因为该男子只是在讲述事情，并没有照片和视频等证据，所以不少网友们都将信将疑。很快谭咏麟的工作室以及谭咏麟本人都出面发声了，并且否认此事情，表示谭咏麟校长一直都是尊重粉丝的，网友在胡编乱造，保留追究其责任的权利。\"},{\"type\":\"text\",\"value\":\"虽然谭咏麟否认了，但是该名男网友很快就发照片和聊天截图出来，并且还放狠话表示：敢发声就再锤一次。男网友晒出的照片里谭咏麟正在脱裤子，并且还有脱完裤子的照片，甚至还有坐在床上的照片，一时间也是引起网友们的热议和围观，这是什么情况呢？难道谭咏麟真的睡了小自己48岁的女粉丝吗？\"},{\"type\":\"text\",\"value\":\"根据该名男子爆料，自己的女友一直瞒着自己说去上海找闺蜜住几天，不过一直觉得女友在撒谎，偶然间翻看女友手机才知道女友去找谭咏麟，并且得知当时谭咏麟确实也在上海。所以这一切令自己十分伤心难过，更是直接质疑谭咏麟：你怎么下得去手！\"},{\"type\":\"text\",\"value\":\"在该名男网友爆料不久，事情就被传开了，随后还有应采儿点赞了相关的事情报道。不知道应采儿是在放实锤还是手滑了呢？不少网友们也是纷纷喊话应采儿：是不是知道什么内幕呢？求放实锤。\"},{\"type\":\"text\",\"value\":\"谭咏麟如今71岁，在香港乐坛乃至整个娱乐圈都是德高望重的人，网友们都十分尊敬他并且喊他为谭校长。谭咏麟不仅在娱乐器德高望重，并且还是以为爱国的好艺人，所以粉丝众多。对于男粉丝爆料出来这样的事情，着实令人有点吃惊。网友们也是喊话谭咏麟肖战赶紧出来澄清一下。\"},{\"type\":\"text\",\"value\":\"目前为止谭咏麟以及工作室都没有再一次发声，不少网友们也是质疑是不是太担心了？还是另有原因呢？在当事人没有再一次发声回应之前，希望网友们也不要胡乱猜测，避免给人造成不必要的伤害。\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"},{\"type\":\"text\",\"value\":\"(责任编辑：彭欣怡_NBJS16638)\"}]',0,6,'爬虫','2021-11-26 15:41:57','2021-11-26 15:41:57',2,'2021-11-26 15:41:57','文本内容包含自管理的敏感词: {66=1}',NULL,'',1),(6297,1102,'谭咏麟睡粉事件新进展 爆料者：前女友已坦诚一切','[{\"type\":\"text\",\"value\":\"71岁谭咏麟被爆“睡粉”的事已经发酵了两天。\"},{\"type\":\"text\",\"value\":\"爆料者从最初愤怒发文直指谭咏麟艺德有亏，到谭咏麟方发布否认声明后，又相继爆出疑似谭咏麟酒店背影照及通话记录截图，再到怒怼质疑他的谭咏麟粉丝，随后又毫无征兆地删除了一些爆料帖。\"},{\"type\":\"text\",\"value\":\"这件事似乎也变得更加扑朔迷离。\"},{\"type\":\"text\",\"value\":\"首先，爆料者给人的感觉很激动，言辞间无不透露着对谭咏麟的愤怒，但他能够证明所言非虚的材料，只有一篇控诉文和看不清人脸的背影照。这些“证据”对他的爆料内容来说，根本就是毫无力度的锤。\"},{\"type\":\"text\",\"value\":\"其次，对于一些信奉“凡事都不会是空穴来风”的人来说，有瓜有料的指控自然不可能是无端出现，更何况，爆料者的表现不像是在炒作。\"},{\"type\":\"text\",\"value\":\"以至于在爆料者删除部分帖子后，一些网友开始猜测他是不是被公关了，即“是不是这件事已经在私下谈妥了？”\"},{\"type\":\"text\",\"value\":\"就在大家疑惑不解时，11月25日，事情终于又有了新进展。\"},{\"type\":\"text\",\"value\":\"该爆料者发文正面回应各种质疑，声称没有被公关，而且也不接受公关。此前只是太气愤才宣泄出来，只是没想到占用了这么多公共资源。\"},{\"type\":\"text\",\"value\":\"他还表示，前女友已经向自己坦诚了一切，对于此前发布过的所有内容，他都会负法律责任，不论对方采取什么手段，自己都会奉陪到底。\"},{\"type\":\"text\",\"value\":\"从爆料者的态度来看，无疑还是一如既往地强硬，一句“为言论负责，奉陪到底”既表明了自己的态度，又间接在佐证自己爆料内容的真实。\"},{\"type\":\"text\",\"value\":\"不过也有网友从这段文字中发现一些值得推敲的细节：\"},{\"type\":\"text\",\"value\":\"首先，他对女方的称呼已经变成了“前女友”，这意味着经过此前的爆料，二人已经分道扬镳。\"},{\"type\":\"text\",\"value\":\"“分手”看似是情感问题，却也在间接证明他所爆料的内容并非虚假杜撰，他也并不是所谓的“炒作”。\"},{\"type\":\"text\",\"value\":\"但其次，他说“没想到会占用这么多公共资源”这句话却不免让人起疑。\"},{\"type\":\"text\",\"value\":\"谭咏麟的名气众所周知，一个老牌巨星被质疑“睡粉”这种事的影响力更是可想而知，所以有网友怀疑：\"},{\"type\":\"text\",\"value\":\"他说这句话的目的，要么是主动找台阶希望让事件平息；要么就是被公关了，钱到手了，以此言论妄图息事宁人。\"},{\"type\":\"text\",\"value\":\"不过在我看来，事情或许不是网友猜测的“被公关”，毕竟他最后那句话很明显是意有所指，就是在肯定自己爆料内容的真实性。\"},{\"type\":\"text\",\"value\":\"所以，如果这样也算“被公关”的话，只能说公关得很失败，承认了事实又侧面肯定了爆料者的强硬态度，拉拢人心。\"},{\"type\":\"text\",\"value\":\"唯一的可能就是，他确实不想把事情闹得太大，再加上他曾表示有谭咏麟粉丝对其进行言语攻击，所以不堪其扰下，希望在坚定立场的前提下回归正常生活。\"},{\"type\":\"text\",\"value\":\"不过这些都是我们的猜测，按照事件最新进展中爆料者的言论来看，这件事能否到此为止，取决于“德艺双馨”方的后续态度。\"},{\"type\":\"text\",\"value\":\"而整件事的真实情况到底如何？他没拿出实锤，谭咏麟方也发声辟谣，所以作为吃瓜群众的我们，还是理性吃瓜，静待后续吧！\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:41:58','2021-11-26 15:41:58',9,'2021-11-26 15:41:58','阿里云调用失败，转人工审核',1464138458938236930,'',1),(6298,1102,'李沁穿黑色西装内搭吊带衣 眼神撩人','[{\"type\":\"text\",\"value\":\"测试文章内容\"}]',0,6,'爬虫','2021-11-26 15:41:59','2021-11-26 15:41:59',9,'2021-11-26 15:41:59','阿里云调用失败，转人工审核',1464138459122786306,'',1),(6299,1102,'姚贝娜病逝6年多了 年迈父母的晚年生活状况怎样？','[{\"type\":\"text\",\"value\":\"点击关注，每天都有名人故事感动您！\"},{\"type\":\"text\",\"value\":\"姚贝娜与父母\"},{\"type\":\"text\",\"value\":\"2015年1月16日，著名歌手姚贝娜因乳腺癌不幸在深圳病逝，年仅33岁。姚贝娜的早逝，是国内流行乐坛的巨大损失，也让众多歌迷心痛泪奔。而姚贝娜的父亲姚峰和母亲李信敏，更是沉浸在悲痛中无法自拔。\"},{\"type\":\"text\",\"value\":\"而今6年多过去了，姚峰和妻子走出了丧女悲痛了吗？他们以怎样的方式怀念天堂里的女儿？\"},{\"type\":\"text\",\"value\":\"01\"},{\"type\":\"text\",\"value\":\"白发人送黑发人，是人间最撕裂的悲痛。2015年1月16日，是姚峰和妻子最黑暗最痛心的一天。爱女离他们而去，姚峰夫妇感觉天塌了，心里空了，泪水将他们淹没。\"},{\"type\":\"text\",\"value\":\"6年多来，姚峰和妻子无时无刻不在怀念女儿，一个个漫漫长夜里，他们与女儿走过的那些泪与痛的岁月，一幕幕在脑海里回放……\"},{\"type\":\"text\",\"value\":\"姚贝娜\"},{\"type\":\"text\",\"value\":\"姚峰夫妇与女儿\"},{\"type\":\"text\",\"value\":\"姚贝娜1981年9月26日出生于湖北省武汉市，是家里的独生女儿。父亲姚峰1953年出生于武汉，19岁考入武汉音乐学院作曲系，后转入声乐系。\"},{\"type\":\"text\",\"value\":\"大学毕业后，姚峰留在母校任教。1989年，武汉音乐学院率先成立通俗演唱专业，姚峰出任声乐系副主任，培养出了雷湘、黄鹤翔等著名弟子。\"},{\"type\":\"text\",\"value\":\"姚峰是音乐天才，创作的《又见西柏坡》《乡情》《中国英雄》《我生在1978》《我爱中国》《一路有你》《祖国，深圳对你说》等歌曲，有着广泛的影响。\"},{\"type\":\"text\",\"value\":\"姚贝娜的母亲李信敏也毕业于武汉音乐学院声乐系，与丈夫是大学同学。大学毕业后，李信敏被分配到湖北省歌剧舞剧院担任独唱演员。\"},{\"type\":\"text\",\"value\":\"她演唱的《我心中有一首赞美的歌》《百灵鸟你莫啼叫》等原创歌曲，深受歌迷喜爱。\"},{\"type\":\"text\",\"value\":\"青年李信敏\"},{\"type\":\"text\",\"value\":\"姚贝娜遗传了父母优良的艺术基因，4岁开始学习钢琴，9岁登台演唱，并多次在全国省市歌唱比赛中获奖。\"},{\"type\":\"text\",\"value\":\"1997年，姚贝娜考入武汉音乐学院附中，开始在父亲手下学声乐。期间，姚贝娜参加“大红鹰杯”全国青年歌手大奖赛，一举夺得湖北赛区专业组通俗唱法亚军。随后她代表湖北队参加全国总决赛，夺得优秀奖。\"},{\"type\":\"text\",\"value\":\"1998年，45岁的姚峰被深圳作为特殊人才引进，担任深圳市群艺馆馆长，后又出任深圳市文化局艺术处处长、市音协主席等职。\"},{\"type\":\"text\",\"value\":\"为追随丈夫，1999年李信敏也从武汉调入深圳艺术学校，从事声乐教学工作。这样一来，小小年纪的姚贝娜与父母分隔在两个城市。她吃住在学校，每年只有寒暑假才去深圳与父母团聚。\"},{\"type\":\"text\",\"value\":\"2000年，姚贝娜考入北京的中国音乐学院，大学毕业后进入海政文工团，担任独唱演员。\"},{\"type\":\"text\",\"value\":\"02\"},{\"type\":\"text\",\"value\":\"姚贝娜是一位天生的歌者，音域宽广，音色嘹亮独特，高音区游刃有余。成为专业歌手后，姚贝娜开启了自己辉煌而短暂的人生。\"},{\"type\":\"text\",\"value\":\"2006年，她夺得全国青年歌手电视大奖赛专业组通俗唱法亚军。2008年，她再次夺得全国青年歌手电视大奖赛专业组通俗唱法冠军。\"},{\"type\":\"text\",\"value\":\"她演唱的电视剧《甄嬛传》片头曲《红颜劫》、片尾曲《菩萨蛮》，以及歌曲《生命的河》《画情》《小头发》《又忆江南》《心火》等，深受歌迷喜爱。姚贝娜也成为当时最红、人气最旺的歌手之一。\"},{\"type\":\"text\",\"value\":\"姚峰与女儿\"},{\"type\":\"text\",\"value\":\"姚贝娜一直是父亲的骄傲，他们既是父女又是师生，还是音乐上的知己。每次一家三口团聚，大家谈的都是音乐，并不是你清唱一段，我清唱一段，欢笑声、歌声弥漫家里的每一寸空间。\"},{\"type\":\"text\",\"value\":\"2011年，姚贝娜30岁了，该谈恋爱了。这些年因忙于求学，打拼事业，姚贝娜无暇顾及感情问题。\"},{\"type\":\"text\",\"value\":\"随着女儿年龄一天天增大，姚峰和妻子也开始催婚。在他们看来，女孩子唱歌与谈恋爱、结婚并不矛盾。相反家庭幸福，更会成为事业的助推器。\"},{\"type\":\"text\",\"value\":\"特别是女儿跨入30岁门槛后，姚峰与妻子跟女儿进行了一次认真的谈话，希望她唱好歌的同时，也能收获一份美满的爱情。\"},{\"type\":\"text\",\"value\":\"姚贝娜生活照\"},{\"type\":\"text\",\"value\":\"姚贝娜与刘欢\"},{\"type\":\"text\",\"value\":\"是呀，该将爱情摆上议事日程了，这是对自己和父母的交代，也是完美人生的延伸。姚贝娜告诉父母：“你们别操心，以前我是与音乐谈恋爱，以后会找个好男孩，好好享受恋情。”\"},{\"type\":\"text\",\"value\":\"有了女儿这句话，姚峰夫妇心里踏实了。接下来的日子里，夫妇俩一直在深圳等女儿的喜讯，等她带着称心的男友回家。\"},{\"type\":\"text\",\"value\":\"然而2011年5月30日，姚峰夫妇接到女儿从北京打来的一个电话，顿时心如刀割，痛心的泪水簌簌而落。\"},{\"type\":\"text\",\"value\":\"原来早在一个月前，姚贝娜发现自己的左乳有几块类似酒窝的浅淡痕迹。她去北京几家医院做检查，医生说是炎症。\"},{\"type\":\"text\",\"value\":\"姚贝娜\"},{\"type\":\"text\",\"value\":\"20多天后，姚贝娜身体上的症状越来越明显，她再次去医院检查，医生这才确诊她患的是肿瘤。要想痊愈，必须切除病灶。\"},{\"type\":\"text\",\"value\":\"03\"},{\"type\":\"text\",\"value\":\"身体残缺，对一个未婚姑娘是多么大的打击！姚贝娜在泪水中纠结了数天，最终求生的愿望战胜了所有顾虑，她决定接受病灶切除手术。\"},{\"type\":\"text\",\"value\":\"医生根据她的身体状况，将手术日期定在5月31日。面对生命劫难，姚贝娜格外想念父母，想念他们的温暖和心理支撑。于是她给远在深圳的父母打电话，如实讲述了自己的病情，及手术日期。\"},{\"type\":\"text\",\"value\":\"只是姚贝娜没想到，父母接到她的电话，内心引发山崩海啸般的地震。李信敏眼前一黑，差点摔倒在地。姚峰赶紧将妻子抱住，夫妻俩久久没有说话，两双眼睛里都是泪水。\"},{\"type\":\"text\",\"value\":\"姚峰夫妇连夜订飞机票，本来飞机是凌晨3点的，但夫妇俩晚上10点就赶到了机场。他们多么想早一点见到女儿，给她送去温暖与力量。\"},{\"type\":\"text\",\"value\":\"次日上午，姚峰夫妇到达了医院。在门口姚峰对妻子说：“见到女儿，咱俩谁也不能哭。”李信敏带着哭腔点头道：“我不会哭。”一语未了，她哭了起来，姚峰也泪流满面。\"},{\"type\":\"text\",\"value\":\"姚峰夫妇见到女儿后，她已经被麻醉推入手术室。姚贝娜接受了病灶切除手术，以及整形再造手术，手术持续了8个多小时。\"},{\"type\":\"text\",\"value\":\"当姚贝娜从麻醉中清醒过来时，第一句话就是向父母道歉：“爸爸妈妈，对不起，让你们担心了。”\"},{\"type\":\"text\",\"value\":\"姚峰和妻子紧紧握住女儿的手：“安心养病，黑暗终于过去了。”姚贝娜露出一丝坚强的笑容：“我会好起来的，我对自己有信心。”姚峰夫妇一直在医院里陪护女儿，直达姚贝娜出院。\"},{\"type\":\"text\",\"value\":\"姚贝娜在春晚上演唱《天耀中华》\"},{\"type\":\"text\",\"value\":\"姚峰和妻子都有一摊子事要忙，不可能长期留在北京陪女儿。不久他们返回深圳，姚贝娜一边在北京康复，一边工作。\"},{\"type\":\"text\",\"value\":\"2012年，姚贝娜推出全新专辑《小头发》，还参加了《中国好声音》比赛。\"},{\"type\":\"text\",\"value\":\"2014年，姚贝娜登上央视春节联欢晚会，压轴演唱《天耀中华》。这首歌旋律优美，雄浑大气，难度相当大，姚贝娜演绎得堪称完美。\"},{\"type\":\"text\",\"value\":\"除夕之夜，姚峰夫妇坐在电视机前收看，想起女儿抗击癌魔的不易，夫妇俩热泪盈眶。\"},{\"type\":\"text\",\"value\":\"手术后，姚贝娜感觉身体状况越来越好，每次打电话，她都给父母报喜，说自己浑身有力量，吃饭有胃口。姚峰夫妇期盼女儿早日彻底康复，早日与爱情拥抱。\"},{\"type\":\"text\",\"value\":\"04\"},{\"type\":\"text\",\"value\":\"然而，灾难又不知不觉降临了。2014年12月，癌细胞开始在姚贝娜的体内扩散，并转入到大脑和肺部。姚峰夫妇将女儿从北京接回深圳。\"},{\"type\":\"text\",\"value\":\"2015年1月，姚贝娜住进了深圳一家医院。1月16日，姚贝娜因晚期癌症引发脏器衰竭不幸病逝，年仅33岁。\"},{\"type\":\"text\",\"value\":\"临终前，姚贝娜伸出枯瘦的手，搭在父母的手背上：“爸爸妈妈，对不起，我还没有给你们尽孝，就要走了，来生我们再成为一家人，我走后你们要坚强！”姚峰夫妇的心碎裂一地。\"},{\"type\":\"text\",\"value\":\"女儿早逝，让姚峰和妻子感觉天塌了。送别女儿，他们久久沉浸在悲痛中不能自拔。夫妇俩经常在家里流泪，不吃不喝，失眠，感觉整个人都被掏空了。\"},{\"type\":\"text\",\"value\":\"女儿的音容笑貌，女儿病中的坚强，女儿站在舞台上演出的美丽身影……一遍遍在姚峰脑海里回放着。女儿仿佛就生动地站在他们面前，而斯人已逝，夫妇俩怎能接受？\"},{\"type\":\"text\",\"value\":\"姚峰\"},{\"type\":\"text\",\"value\":\"姚峰夫妇的亲友及学生，纷纷登门看望他们，给予他们温暖和开导。学生们劝姚峰夫妇：“贝娜是天生的歌者，是音乐的化身，现在她的身影化作音符飞到天国去了，我们在音乐中怀念她吧。”\"},{\"type\":\"text\",\"value\":\"是呀，女儿将音乐看得重于生命，她在生时，一家人团聚谈的都是音乐。音乐是姚贝娜与父母联系的纽带，现在她走了，姚峰和妻子决定在音乐中怀念女儿，寄托对女儿的思念。\"},{\"type\":\"text\",\"value\":\"此后姚峰夫妇将泪和痛埋在心底，开始以音乐这种特殊的媒介怀念女儿。\"},{\"type\":\"text\",\"value\":\"05\"},{\"type\":\"text\",\"value\":\"2015年，在女儿去世半年后，姚峰接受湖南知青艺术团的邀请，指挥他们演唱自己创作的歌曲《我们曾年轻》。\"},{\"type\":\"text\",\"value\":\"姚峰夫妇参加活动\"},{\"type\":\"text\",\"value\":\"2016年，在深圳“十月音乐汇”上，姚峰又在深圳大剧院担任指挥，指挥爱乐乐团演奏19首歌曲。他还应邀在深圳交响乐团担任指挥。\"},{\"type\":\"text\",\"value\":\"2020年9月，姚峰和妻子录制了《留存》《等不到雪》《放风筝》《月光下的红树林》等6首歌曲，其中5首是姚峰的作品。夫妻俩将这6首歌曲献给天堂里的女儿。\"},{\"type\":\"text\",\"value\":\"平时想女儿了，姚峰和妻子就会在家里听姚贝娜的歌。听着听着，很多美好的往事就会涌上心头。\"},{\"type\":\"text\",\"value\":\"每当这时，李信敏就会哭着说：“要是贝娜当年早点结婚，留下个孩子就好了，我们也会有更多的念想。”\"},{\"type\":\"text\",\"value\":\"人生有太多的遗憾和意外，姚峰也没想到女儿的一生是如此辉煌，又是如此短暂。\"},{\"type\":\"text\",\"value\":\"夫妇俩在女儿雕塑前\"},{\"type\":\"text\",\"value\":\"为纪念姚贝娜光辉短暂的一生，相关部门在武汉石门峰纪念公园建造了姚贝娜雕像。姚峰在底座上给女儿写了两句话：“一个爱唱歌的女孩，一个用生命歌唱的女孩。”\"},{\"type\":\"text\",\"value\":\"每年女儿的生日、忌日，姚峰和妻子都会来雕像前献花，絮絮讲述女儿走后自己的生活状况。他们告诫女儿：“我们很好，你在那边别牵挂。！”\"},{\"type\":\"text\",\"value\":\"女儿病逝前，姚峰夫妇根据她的意愿，代女儿签下了眼角膜捐献协议。后来姚贝娜的眼角膜被移植到一个孩子的身上，让他重见了光明。姚峰夫妇得知消息，觉得女儿的生命得到了延续，姚贝娜在用另一双眼睛欣赏这个世界。\"},{\"type\":\"text\",\"value\":\"2021年，姚峰在网上开直播，教学生们声乐。女儿走了，姚峰和妻子将学生们当做自己的孩子，他们教学生们声乐知识，也以音乐这种媒介怀念女儿！\"},{\"type\":\"text\",\"value\":\"-END-\"},{\"type\":\"text\",\"value\":\"原创不易，敬请点赞关注！\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:42:01','2021-11-26 15:42:01',2,'2021-11-26 15:42:01','文本内容包含自管理的敏感词: {33=2}',NULL,'',1),(6300,1102,'“打杂艺人”邹兆龙的爱国情怀','[{\"type\":\"text\",\"value\":\"娱乐明星都想进军“好莱坞”，为了好莱坞的梦想，从不挑选角色，即使在戏里被虐得体无完肤，也心之愿往。\"},{\"type\":\"text\",\"value\":\"可是，在娱乐圈有这样的一位艺人，他进入“好莱坞”就一个要求，演正派人士，而且拒绝“玷污”华人，他就是“邹兆龙”。\"},{\"type\":\"text\",\"value\":\"演了一辈子的“坏蛋”，可是在家国情怀下，他在国外的影片中却很有担当。\"},{\"type\":\"text\",\"value\":\"他说“电影是艺术，我不可能让你们用这样的方式践踏我的民族”。为这样的爱国艺人点赞。\"},{\"type\":\"text\",\"value\":\"01、\"},{\"type\":\"text\",\"value\":\"1976年，邹兆龙出生在台湾省，家中十三个兄弟姐妹，邹兆龙排行老幺。\"},{\"type\":\"text\",\"value\":\"家中的孩子众多，父亲不堪重负，早早去世，这对邹兆龙的成长造成了很大的影响，在他还不懂事的时候，为了家庭妈妈不得已把哥哥、姐姐送到孤儿院，只留下邹兆龙在身边。\"},{\"type\":\"text\",\"value\":\"邹兆龙慢慢 长大，他知道父亲不在了，妈妈也没有太多的精力照顾自己。\"},{\"type\":\"text\",\"value\":\"为了不让小朋友欺负自己，邹兆龙在六岁的时候，在与妈妈一起拜佛的时候，看到了雕刻机“嗡嗡”地转动，他想学习这个技术。\"},{\"type\":\"text\",\"value\":\"妈妈就让他做起了“雕刻学徒”。\"},{\"type\":\"text\",\"value\":\"小孩子的“三分钟”热血，很快就被枯燥的工作磨没了。\"},{\"type\":\"text\",\"value\":\"但是在雕刻店旁边有一间武馆，邹兆龙对武术充满了好奇，总是偷偷地看着武馆里的人“呼呼哈哈”。\"},{\"type\":\"text\",\"value\":\"邹兆龙的好奇，被一位十来岁的大哥哥看到，对他说“你来练武吧，免费的”，邹兆龙就来到了武馆。\"},{\"type\":\"text\",\"value\":\"没想到自己的好奇，会成为自己日后吃饭的本钱。\"},{\"type\":\"text\",\"value\":\"在雕刻店干了三年，邹兆龙对雕刻店的工作厌烦了，就和妈妈吵着说“我不想干了，我想去做面包”，那时，邹兆龙经常吃不饱。\"},{\"type\":\"text\",\"value\":\"他想“面包店”总能吃饱了吧，妈妈也觉得他这么小，干那么累的活，就让他去面包店打工了。\"},{\"type\":\"text\",\"value\":\"在工作之余，邹兆龙都会拿出双节棍在烤箱旁边耍上一会。\"},{\"type\":\"text\",\"value\":\"偶尔还会给同事们表演一下“咏春、练跆拳道、泰拳”等套路武术，同事们对这个小孩都很喜欢。\"},{\"type\":\"text\",\"value\":\"一天，一位同事和他说“我给你介绍了一个工作，给演员做替身”。邹兆龙对表演还没有概念，就带着很多问号前去片场。\"},{\"type\":\"text\",\"value\":\"在片场，导演和他讲了很多规则，本以为会NG几次，哪成想倪星超乎想象地让大家很满意，导演向他伸出橄榄枝，说“有没有兴趣拍电影”？\"},{\"type\":\"text\",\"value\":\"邹兆龙觉得很好玩，就同意了。\"},{\"type\":\"text\",\"value\":\"02、\"},{\"type\":\"text\",\"value\":\"邹兆龙问导演自己的酬劳是多少时，导演笑着拿出了1200块台币，而倪星那会在面包店一个月才9000块台币。\"},{\"type\":\"text\",\"value\":\"不过，他不知道武行是“非常辛苦的行业”，僧多粥少，很长时间都排不上角色，为了生计，邹兆龙买了折叠床，住在了剧组。\"},{\"type\":\"text\",\"value\":\"那时，拍一部戏，就有1000块的收入，有时一天要拍三四部戏，每天就有几千块的收入，这对邹兆龙来说“钱太容易挣了”。\"},{\"type\":\"text\",\"value\":\"邹兆龙虽然很小，但是很懂得行规，每天起得很早，从来不耽误拍戏，往往都是最早到的剧组，没开拍的时候，就自己在片场练功。\"},{\"type\":\"text\",\"value\":\"好心总会有好报，邹兆龙靠着勤奋让圈内人认可了邹兆龙。\"},{\"type\":\"text\",\"value\":\"在替身行业一干就是几年，也攒下了一些积蓄，给妈妈买了房子。\"},{\"type\":\"text\",\"value\":\"等他到了18岁，一个贵人出现了，改变了他的一生。\"},{\"type\":\"text\",\"value\":\"1985年，邹兆龙被知名艺人推荐，成为电影《全力反弹》的演员，当时《全力反弹》的导演是洪金宝。\"},{\"type\":\"text\",\"value\":\"洪金宝需要一位“有格斗经验、会武术套路、有跆拳道功底”的人，邹兆龙来到片场，洪金宝开始还很不以为然，试戏的时候，洪金宝才发现“这是个宝贝呀”，什么都会，而且功夫不错。\"},{\"type\":\"text\",\"value\":\"就这样，邹兆龙拍摄了这部电影。\"},{\"type\":\"text\",\"value\":\"而后，邹兆龙服兵役，洪金宝非常赏识邹兆龙，就对他说“我等你”。\"},{\"type\":\"text\",\"value\":\"两年的时间，转瞬即逝，邹兆龙退役之后，洪金宝一个电话说“来香港吧”，随后把邹兆龙的关系等事宜都办理好。\"},{\"type\":\"text\",\"value\":\"邹兆龙成为了“洪家班”的一员，也拜师洪金宝。\"},{\"type\":\"text\",\"value\":\"03、\"},{\"type\":\"text\",\"value\":\"洪金宝对邹兆龙很关心，不仅教他做武术指导，还教他执导拍戏，而且对做人做事上，也给了他很多经验。\"},{\"type\":\"text\",\"value\":\"那时，邹兆龙觉得洪金宝就是自己的半个“爸爸”。\"},{\"type\":\"text\",\"value\":\"在香港早期的娱乐圈，有一个不成文的规定，那就是“导演不找你，你不能找导演约戏”。\"},{\"type\":\"text\",\"value\":\"邹兆龙很懂规矩，在片场首先向前辈问好，之后再与导演、武指导交流戏份。\"},{\"type\":\"text\",\"value\":\"武打演员在那会必须要真打，还要会“挨打”，没有套路，邹兆龙在这方面很“胆大”，导演说开始，他的命就是导演的，即使悬崖绝壁，说跳就跳。\"},{\"type\":\"text\",\"value\":\"在拍戏的时候，经常受伤，但是他从不喊疼。\"},{\"type\":\"text\",\"value\":\"“洪家班”人才济济，邹兆龙只能从替身、配角中锤炼自己的演技。\"},{\"type\":\"text\",\"value\":\"早期洪家班拍摄了很多“鬼片”，像《羞羞鬼》、《怕羞鬼》、《鬼咬鬼》、《龙凤贼捉贼》等，都是替身、配角。\"},{\"type\":\"text\",\"value\":\"1991年，邹兆龙还是“金牌替身”，在拍摄《一刀倾城》的时候，邹兆龙为了关之琳差点“摔死”。\"},{\"type\":\"text\",\"value\":\"当时，邹兆龙是该部影片的武术指导，还客串了一个小角色，但是关之琳的武替，完成不了动作，邹兆龙决定自己上阵，\"},{\"type\":\"text\",\"value\":\"一个镜头之后，大家觉得不够完美，邹兆龙又来一次，镜头下的他完成的非常出色，却重重的摔在墙上，摔成了重伤。\"},{\"type\":\"text\",\"value\":\"在担架上的邹兆龙对着师傅说“对不起，我得去医院了”，洪金宝握着他的手说“你好好养伤，我们还要一起打天下”。\"},{\"type\":\"text\",\"value\":\"04、\"},{\"type\":\"text\",\"value\":\"邹兆龙在医生建议“卧床半年养伤”时，三个月火速出院，直奔片场，投入工作，洪金宝看徒弟这样卖命就给他安排三部戏，做正派主角。\"},{\"type\":\"text\",\"value\":\"可是，反响平平。\"},{\"type\":\"text\",\"value\":\"1994年，大红大紫的李连杰在拍摄《中南海保镖》的时候，剧组把邹兆龙推荐出来，让他与李连杰演对手戏。\"},{\"type\":\"text\",\"value\":\"两人都是有功夫底子的人，李连杰是武数冠军，对套路很在行，邹兆龙是实打型艺人，更是有多年的替身经历。\"},{\"type\":\"text\",\"value\":\"为了突出李连杰的功夫，与邹兆龙的对打戏非常多，而邹兆龙饰演的“反派”，给大众留下了深刻的印象，不仅功夫好，而且那犀利的眼神，坏坏的神情，都拿捏的很到位。\"},{\"type\":\"text\",\"value\":\"李连杰也开始欣赏这个“坏蛋”了。\"},{\"type\":\"text\",\"value\":\"后来，李连杰在拍摄《倚天屠龙记之魔教教主》，向导演推荐邹兆龙出演“宋青书”，两人一正一邪，组成了鲜明的对比。\"},{\"type\":\"text\",\"value\":\"不过这部戏的精彩角色太多，邹兆龙 反派也让人反感，反而“张敏、李连杰、邱淑贞、黎姿”等人大红大紫。\"},{\"type\":\"text\",\"value\":\"邹兆龙出色的演技赢得了“周星驰”的青睐，邀请他来拍摄自己的《九品芝麻官之白面包青天》。\"},{\"type\":\"text\",\"value\":\"邹兆龙对“喜剧”不是很感冒，他觉得演戏就要真刀真枪的，喜剧太浮夸。\"},{\"type\":\"text\",\"value\":\"可周星驰几次邀请，还求到了洪金宝，搬出了很多前辈，最终邹兆龙出演了。\"},{\"type\":\"text\",\"value\":\"他在戏里演了一个“大坏蛋”常威。\"},{\"type\":\"text\",\"value\":\"邹兆龙把“常威”这个角色演的坏到了极点，剧中的台词也成为了经典“我看到常威在打来福，常威你还说你不会武功”。\"},{\"type\":\"text\",\"value\":\"无心插柳柳成荫，邹兆龙靠着“坏蛋”被冠上了“反派专业户”的帽子。\"},{\"type\":\"text\",\"value\":\"他无意之间就爆红 ，不过有些观众太入戏，把他看成了真常威，经常被骚扰，邹兆龙就在采访的时候说“常威是我双胞胎弟弟”。\"},{\"type\":\"text\",\"value\":\"之后的他一直演是“反派角色”。\"},{\"type\":\"text\",\"value\":\"那时，邹兆龙也赚得盆满钵满，片酬也到了几十万一部。\"},{\"type\":\"text\",\"value\":\"05、\"},{\"type\":\"text\",\"value\":\"踌躇满志的青年，在没有事业上升的时候，很失落。\"},{\"type\":\"text\",\"value\":\"1994年，邹兆龙与港姐亚军翁慧德撞个满怀，两人很快就恋爱了，不过为了事业，两人都不承认。\"},{\"type\":\"text\",\"value\":\"直到1996年，邹兆龙才承认与翁慧德相恋了四年。\"},{\"type\":\"text\",\"value\":\"当时很少人看好这段感情，毕竟那时的港姐都是“大佬”的玩物。\"},{\"type\":\"text\",\"value\":\"可是邹兆龙很在意恋情，从小就缺少爱与被爱，所以他对翁慧德很好。\"},{\"type\":\"text\",\"value\":\"1999年，邹兆龙与翁慧德多年恋情修成正果，两人前往美国注册结婚，从小就没怎么上过学的邹兆龙在美国都张不开嘴。\"},{\"type\":\"text\",\"value\":\"于是就自学英文，由于很好学，再加上勤奋，没多久邹兆龙就能与人正常交流。\"},{\"type\":\"text\",\"value\":\"2000年，身在美国的袁和平邀请邹兆龙加入《黑客帝国》剧组，参与武术指导和演员训练。\"},{\"type\":\"text\",\"value\":\"邹兆龙首先看了剧本，看到细节中没有“践踏民族”的戏份，决定参演。\"},{\"type\":\"text\",\"value\":\"邹兆龙有功夫，还有演技，再加上有一股狠劲，一起的演员都非常欣赏他，投资方也对邹兆龙很欣赏。\"},{\"type\":\"text\",\"value\":\"在第二部拍摄的时候，还是邀请了邹兆龙，更是给了邹兆龙“永久分红权”。\"},{\"type\":\"text\",\"value\":\"只要电影有“收益”他就享受分红的权利，哪怕自己不在了，儿女都可以继承这个权利。\"},{\"type\":\"text\",\"value\":\"邹兆龙在后来的采访中说：“在内地华人市场，找我演反派无所谓。华人在各地都在努力树立良好的华人形象，哪怕你给我再多的片酬，我也不能破坏他们辛苦维护的华人形象……艺术不分贵贱，但是你用艺术来践踏我们民族，我就不可能接受。”\"},{\"type\":\"text\",\"value\":\"不过，邹兆龙说“好莱坞学不到电影的魂”。\"},{\"type\":\"text\",\"value\":\"这些年中，邹兆龙虽然看上去坏坏的，但是他很注重电影的内涵，他对自己的要求很高，他说“没有意义的电影自己不去拍”。\"},{\"type\":\"text\",\"value\":\"李连杰对邹兆龙很敬佩，在拍摄《霍元甲》的时候，李连杰推荐了邹兆龙，之后两人合作了很多次。\"},{\"type\":\"text\",\"value\":\"2008年邹兆龙参演了《导火线》，剧中的他虽然是个“坏蛋”，但是他对母亲很是尊重，并不是坏到骨子里，他的洗礼有亲情、有尊重，很有层次。\"},{\"type\":\"text\",\"value\":\"而后，邹兆龙被大众逐渐接受，即使扮演正派人物也很受欢迎，在《四大名捕》中就很出色。\"},{\"type\":\"text\",\"value\":\"不仅这样，邹兆龙还是一个“戏托”，在《全城戒备》中邹兆龙的角色最为出彩。\"},{\"type\":\"text\",\"value\":\"2011年，邹兆龙拍摄了《铁血娇娃之丛林日记》，带着一群妹子，在丛林里……\"},{\"type\":\"text\",\"value\":\"06、\"},{\"type\":\"text\",\"value\":\"2016年邹兆龙主演了《超级保镖》，饰演了一位“穷凶极恶”的大坏蛋，角色饰演得深入人心。\"},{\"type\":\"text\",\"value\":\"邹兆龙在角色上拿捏得很到位，是圈里公认的敬业，在观众心中，他更是“男神”，可是人红是非多……\"},{\"type\":\"text\",\"value\":\"2017年08月26日晚上，微博上有一位叫“邹兆龙出轨记录”的微博引起了关注。\"},{\"type\":\"text\",\"value\":\"微博里详细爆料了邹兆龙与她交往的种种细节，并曝出与她交往后还出轨多人。\"},{\"type\":\"text\",\"value\":\"微博主就是其中之一，而且这种爆料在14日就陆续开始了，该女子称自己是在被“灌醉”的情况下发生的“关系”。\"},{\"type\":\"text\",\"value\":\"爆料者称邹兆龙还在事后给自己“洗内裤”，之后甜言蜜语地发誓……\"},{\"type\":\"text\",\"value\":\"邹兆龙随后发声称“自己在美国拍戏，出了车祸”，对发生这样的事，首先向大众道歉，虽然自己不知道是怎么回事，但是自己的事影响了大众的心情，也向妻子道歉，而后把所有的事以及证据交给了律师。\"},{\"type\":\"text\",\"value\":\"在邹兆龙与这位爆料者的证据之下，证明了该女子是“无中生有，为了博人眼球”，爆料女单方面的炒作，最终自食其果，向邹兆龙先生道歉，也得到了法律的制裁。\"},{\"type\":\"text\",\"value\":\"邹兆龙也向媒体说“我们（妻子）以后会珍惜彼此”。\"},{\"type\":\"text\",\"value\":\"这件事之后，大家对邹兆龙另眼相看，他很有责任感，对家庭、婚姻很忠诚。\"},{\"type\":\"text\",\"value\":\"在接受采访中，邹兆龙说“自己会继续拍戏，直到不能动”。\"},{\"type\":\"text\",\"value\":\"多年的拍戏生涯，他有很多伤，他从来都没有抱怨过一句。\"},{\"type\":\"text\",\"value\":\"纵使在大众心里他是最大的坏蛋，但是他却是个十足的好人。\"},{\"type\":\"text\",\"value\":\"2021年，邹兆龙撞脸“体操冠军刘洋”，网友还说“常威”还说你不会武功……\"},{\"type\":\"text\",\"value\":\"刘洋自己在媒体上发文说“你们不要叫我常威了”。\"},{\"type\":\"text\",\"value\":\"邹兆龙也发图回应。\"},{\"type\":\"text\",\"value\":\"而后网友隔空喊话邹兆龙“你能打赢甄子丹吗”？邹兆龙回应“我一定能”。\"},{\"type\":\"text\",\"value\":\"人生如戏，邹兆龙从艺几十年，一直都是反派，但是在观众的心中他是坏蛋，却也是实力演员，从他对民族大义上，他是个合格的演员，对角色上的拿捏，他是个实力艺人，对待感情的忠诚度上，他有责任，没有花料。\"},{\"type\":\"text\",\"value\":\"实力派加上演技派，这才是邹兆龙，这才是合格的艺人。\"},{\"type\":\"text\",\"value\":\"对于这样的邹兆龙大家还有什么补充的吗？欢迎评论区留言讨论。\"},{\"type\":\"text\",\"value\":\"本文由文刀万原创，欢迎关注，带你一起长知识！\"},{\"type\":\"text\",\"value\":\"（原创文章，请勿抄袭，违者必究）\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:42:02','2021-11-26 15:42:02',9,'2021-11-26 15:42:02','阿里云调用失败，转人工审核',1464138459319918594,'',1),(6301,1102,'张柏芝穿黑灰色毛衣 长发飘飘气质优雅','[{\"type\":\"text\",\"value\":\"测试文章内容\"}]',0,6,'爬虫','2021-11-26 15:42:03','2021-11-26 15:42:03',9,'2021-11-26 15:42:03','阿里云调用失败，转人工审核',1464138459479302146,'',1),(6302,1102,'少女时代Yuri确认没有感染新冠 将恢复正常活动','[{\"type\":\"text\",\"value\":\"网易娱乐11月26日报道 近日，因与确诊者路线重合而接受检测的少女时代成员Yuri权俞利确认没有感染新冠病毒肺炎。所属公司SM娱乐今天宣布权俞利确认没有被感染，今后将正常出席活动。\"},{\"type\":\"text\",\"value\":\"25日，Yuri通过自己的Ins Story表示:今天早上在做造型并准备的时候，收到了与新冠确诊患者路线重合的消息。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"权俞利昨天因与确诊者路线重合临时缺席了MBC电视台《放课后的心动》节目发布会，权俞利也亲自在SNS发文解释了未能出席《放课后的心动》发布会的原因，表示自己打过疫苗所以应该不会有什么问题。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：刘舒阳_NBJS16803)\"}]',0,6,'爬虫','2021-11-26 15:42:04','2021-11-26 15:42:04',9,'2021-11-26 15:42:04','阿里云调用失败，转人工审核',1464138459651268610,'',1),(6303,1102,'谭咏麟事件女主曝光？本尊回应了','[{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"近日，一名男网友发长文控诉谭咏麟出轨自己的女友，并且还斥责谭咏麟人前人后各一套。该网友表示谭咏麟如今71岁，而自己的女朋友目前才23岁，质问谭咏麟：你怎么下得去手？对此，不少网友们都前来围观热议。\"},{\"type\":\"text\",\"value\":\"虽然谭咏麟很快就出面发声否认此事，并且工作室也是紧急辟谣，但是该名男网友随后就一次的晒出聊天记录和照片，并且照片里还有谭咏麟脱裤子的照片，令不少网友们都诧异不已，难道谭咏麟真的是与该女粉丝有不当的行为吗？该名男网友还喊话谭咏麟：敢再发声就再锤一次。至今为止，谭咏麟也没有发声第二次。\"},{\"type\":\"text\",\"value\":\"目前网友们都在热议这名女子到底会是谁呢？长相如何身材如何呢？很快就有网友晒出照片，表示视频和照片中的女子就是与谭咏麟出轨的女子。还言之凿凿地描述女子的情况，令网友们将信将疑。\"},{\"type\":\"text\",\"value\":\"从画面来看，这名女子不仅年轻，而且身材也相当傲人，长相甜美。不少网友们都评价这名女子的条件不错。不过也有很多网友们质疑照片的真假，毕竟晒出来的是内衣照，看上去像是一名网红，不像是一般的女网友。\"},{\"type\":\"text\",\"value\":\"果然照片的女主人就出来发声否认了，并且斥责造谣博主赶紧删除自己的照片和视频，否则自己将会起诉该网友。该女子质问网友：现在造谣全凭一张嘴吗？看来这名女子真的是被造谣博主惹怒了，毕竟这也算是污蔑诽谤了。\"},{\"type\":\"text\",\"value\":\"其实这样的事情也时有发生，经常都会有网友们造谣吃瓜时间的女主当事人，给人造成十分不必要的麻烦和苦恼，甚至也造成了感情上的伤害。比如李云迪事件，就有不少网友们发声表示找到女主，但是数次都被当事人给否认了。\"},{\"type\":\"text\",\"value\":\"谭咏麟事件女主曝光？长相甜美身材傲人，本尊紧急发声回应。如今谭咏麟还没有再一次发声，我们尚且不知道事件的来龙去脉，所以不希望网友们妄加评论。更不希望网友们造谣，给人带来不必要的伤害，毕竟网络并非法外之地，还是希望网友们能够遵纪守法。\"},{\"type\":\"text\",\"value\":\"延伸阅读\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"乐坛天王谭咏麟的“粉丝情人”\"},{\"type\":\"text\",\"value\":\"粉丝与自己喜欢的明星是什么关系？\"},{\"type\":\"text\",\"value\":\"很多明星不知道怎么与粉丝相处，有些时候处着处着就处到了床上，在娱乐圈男星睡粉的话题经常出现。\"},{\"type\":\"text\",\"value\":\"无论是几线的明星，都能让粉丝为他脱下“尊严”。\"},{\"type\":\"text\",\"value\":\"昨天，乐坛天王谭咏麟被爆出“睡粉”，女方比他小了四轮，那么，谭天王究竟是怎样的一个人呢？\"},{\"type\":\"text\",\"value\":\"我们一起来看看天王在成长路上的点点滴滴，看完你就知道答案了。\"},{\"type\":\"text\",\"value\":\"01、\"},{\"type\":\"text\",\"value\":\"1950年谭咏麟出生在香港，父亲“谭江柏”是当时知名足球运动员。\"},{\"type\":\"text\",\"value\":\"谭咏麟是家里唯一的男孩，上面有两个姐姐，而后又陆续出生三个妹妹。十来口人都要吃饭，爸爸谭江柏为家人的生计非常辛苦。\"},{\"type\":\"text\",\"value\":\"谭江柏会踢球，与当时的“探长”曾启荣关系非常好，这也让谭咏麟从小就认识了曾志伟。\"},{\"type\":\"text\",\"value\":\"谭咏麟受到父亲的影响也非常喜欢“足球运动”。\"},{\"type\":\"text\",\"value\":\"然而上天是公平的，他在踢球的时候被踢断腿，只能看着别人踢，再加上他从小就长得瘦小。\"},{\"type\":\"text\",\"value\":\"不过，在儿时的时候，谭咏麟与曾志伟是形影不离的“好朋友”，两人一起玩耍，虽然谭咏麟那时家境比不上曾志伟。\"},{\"type\":\"text\",\"value\":\"谭咏麟的学习成绩一般，而且早熟的他在校园里谈起了恋爱。\"},{\"type\":\"text\",\"value\":\"1966年，谭咏麟与同班同学谈着恋爱，感受着“日光浴”。\"},{\"type\":\"text\",\"value\":\"在上大学之前，两人分手，女孩找到了属于自己的幸福，谭咏麟连当备胎的机会都没有了。\"},{\"type\":\"text\",\"value\":\"伤心之余，谭咏麟想用音乐发泄自己的难受。\"},{\"type\":\"text\",\"value\":\"受到西方音乐的刺激，谭咏麟戴上头套，一副披头士的状态，在学校里“哼哼哈嘿……”\"},{\"type\":\"text\",\"value\":\"爸爸想让他学习经济学，从小就很听话的谭咏麟在大学学的经济学。\"},{\"type\":\"text\",\"value\":\"本来可以称为“金融新贵”，上天却安排他与音乐结缘。\"},{\"type\":\"text\",\"value\":\"1968年，谭咏麟与陈友、彭健新、叶智强、陈百祥、陈百燊等组织了一支乐队。\"},{\"type\":\"text\",\"value\":\"几个人就在酒吧做驻唱，收入不错。\"},{\"type\":\"text\",\"value\":\"1970年，他们一起参加“歌唱比赛”，但是没有晋级，几人心情低落到了极点。\"},{\"type\":\"text\",\"value\":\"出门的时候，捡到一张“Loosers”乐队的招募令，捡到便宜的谭咏麟几人前去参加，一下就得到了冠军，也把这个名字作为乐队的名称。\"},{\"type\":\"text\",\"value\":\"好景不长，乐队在成名之后，不到一年，乐队就解散了。\"},{\"type\":\"text\",\"value\":\"当时，陈百祥与“环球小姐”何秀汶谈恋爱，一下上了周刊封面，绯闻满天飞之后，有人挖墙脚。\"},{\"type\":\"text\",\"value\":\"谭咏麟的爸爸也不想让儿子唱歌，想让他读书，就这样谭咏麟前往新加坡读书。\"},{\"type\":\"text\",\"value\":\"其他队员签约了“宝丽金”唱片公司。\"},{\"type\":\"text\",\"value\":\"乐队没了，谭咏麟很伤心，在这会一个“粉丝”走进了他的内心世界。\"},{\"type\":\"text\",\"value\":\"02、\"},{\"type\":\"text\",\"value\":\"在前往新加坡之前，谭咏麟与自己的粉丝“安吉娜”从互动关系，到了交流关系，安吉娜为了谭咏麟也考去上大学。\"},{\"type\":\"text\",\"value\":\"最美的时光就是“有女友陪伴”的日子，谭咏麟在大学的时候，生活得非常惬意，有酒有肉，想唱就唱，怀里还有美娇娘……\"},{\"type\":\"text\",\"value\":\"在大学的时候，谭咏麟就开始组建乐队，玩音乐。\"},{\"type\":\"text\",\"value\":\"大学的时光很快，在大三的时候，谭咏麟染上恶疾，不得已休学在家养病，就在养病期间，谭咏麟内心有一个念头“专心玩音乐，不读书了”。\"},{\"type\":\"text\",\"value\":\"病好之后，谭咏麟真的就不读书了，找了一份“邮递员”的工作，月薪220块。\"},{\"type\":\"text\",\"value\":\"穿梭着街头巷尾的谭咏麟，慢慢地认识了在酒吧做驻唱的几个朋友，自己也喜欢唱歌一来二去，几人就成为了好朋友。\"},{\"type\":\"text\",\"value\":\"谭咏麟辞去了邮递员的工作，转而卖文具。闲暇时就与几个哥们在酒吧做驻唱。\"},{\"type\":\"text\",\"value\":\"但是，谭咏麟心里只有音乐没有女友，安吉娜在谭咏麟身上看不到希望，就在谭咏麟养伤的时候找到了接盘侠。\"},{\"type\":\"text\",\"value\":\"谭咏麟的头上绿油油……\"},{\"type\":\"text\",\"value\":\"不过也遇上了贵人“梁柏涛”，两人早就相识，而后在商议之下，组建新乐队，开始乐队的人还很多，陆陆续续地退出之后，剩下了谭咏麟与陈友、彭健新、叶智强、钟镇涛，组成了“温拿五虎”。\"},{\"type\":\"text\",\"value\":\"乐队在大好形势之下发行了专辑，还登上了颁奖台。\"},{\"type\":\"text\",\"value\":\"1974年，24岁的谭咏麟在名气的加持之下，慢慢地有点小膨胀，再加上自己的发小曾志伟娶妻了，谭咏麟内心的荷尔蒙爆发……\"},{\"type\":\"text\",\"value\":\"在酒吧唱歌的时候，台下的粉丝对他很是欣赏，满眼都是爱意。\"},{\"type\":\"text\",\"value\":\"看在眼里的谭咏麟下台之后，就与这位粉丝牵手成功。\"},{\"type\":\"text\",\"value\":\"这位粉丝就是“杨洁薇”，是一位美容顾问。两人牵手不久就同居一起生活，杨洁薇为了谭咏麟的发展，决心做他背后的女人，辞去公祖哦，照顾谭咏麟的起居。\"},{\"type\":\"text\",\"value\":\"那会谭咏麟的事业正处于发展期间，不能公开感情，杨洁薇也不着急，就帮着他跑前跑后，充当私人助理与形象顾问的角色。\"},{\"type\":\"text\",\"value\":\"谭咏麟的爱情通透之后，事业发展得顺风顺水。\"},{\"type\":\"text\",\"value\":\"“温拿五虎”在乐坛拿奖拿到手软，不过，好景不长，几年之后，钟镇涛退出，这对乐队的打击不小。\"},{\"type\":\"text\",\"value\":\"谭咏麟也“顺坡下驴”，签约了TVB，开始拍戏。\"},{\"type\":\"text\",\"value\":\"因为他的国语不好，在与林凤娇搭戏的时候，被告知回家“练一周，再说”。\"},{\"type\":\"text\",\"value\":\"谭咏麟之后选择了现在小鲜肉的做法，念数字。\"},{\"type\":\"text\",\"value\":\"03、\"},{\"type\":\"text\",\"value\":\"谭咏麟在早期的实力非常强，自己发行了专辑之后，在1981年，凭借《假如我是真的》斩获“金马奖影帝”，\"},{\"type\":\"text\",\"value\":\"那时，林凤娇与成龙相识，曾志伟遇上了娇妻，生下了两个儿子。\"},{\"type\":\"text\",\"value\":\"谭咏麟此时身边还没有“拖油瓶”，看着别人有儿子，自己也想要一个，但是杨洁薇的肚子不给力，几年也没怀上。\"},{\"type\":\"text\",\"value\":\"7年之痒，谭咏麟娶了杨洁薇。\"},{\"type\":\"text\",\"value\":\"婚后为了给孩子更好的未来，谭咏麟忙着办演唱会、发行专辑，基本上没有时间与杨洁薇酝酿“爱的结晶”。\"},{\"type\":\"text\",\"value\":\"谭咏麟在开演唱会的时候一般都是暑假期间，带着学生粉丝一起高歌，也被粉丝冠以“谭校长”的称号。\"},{\"type\":\"text\",\"value\":\"在当时香港的乐坛，有那么几位“大咖”，张国荣就是谭咏麟的劲敌。\"},{\"type\":\"text\",\"value\":\"虽然两人之间没有恩怨，但经纪公司之间的竞争已经开始很久了。\"},{\"type\":\"text\",\"value\":\"陈百祥在做生意成功之后，与谭咏麟的关系非常好，也加入到了“张谭之争中”。\"},{\"type\":\"text\",\"value\":\"1988年，谭咏麟决定退出乐坛，因为唱片公司之间的明争暗斗，让他身心疲惫。\"},{\"type\":\"text\",\"value\":\"不过，他的退出也让张国荣更加难看，一年后，张国荣也封麦……\"},{\"type\":\"text\",\"value\":\"1991年，谭咏麟41岁了，与杨洁薇努力了无数次，也没能怀上宝宝。\"},{\"type\":\"text\",\"value\":\"这对一个中年男人的打击非常大，\"},{\"type\":\"text\",\"value\":\"带着这种心情，在自己的演出舞台下，与当时19岁的女孩朱咏婷，一见倾心。\"},{\"type\":\"text\",\"value\":\"朱咏婷当时还没有大学毕业，也就是个刚刚成年的孩子。\"},{\"type\":\"text\",\"value\":\"四目相对之下，朱咏婷本来就很欣赏谭咏麟，谭咏麟在妻子那得不到的温暖，在朱咏婷身上有了寄托。\"},{\"type\":\"text\",\"value\":\"两人就成为了“秘密情人”。\"},{\"type\":\"text\",\"value\":\"1993年，杨洁薇的父亲去世，丈母娘说“谭咏麟，我女儿跟你这么多年了，你要登报澄清一下我们的关系吧”。\"},{\"type\":\"text\",\"value\":\"第二天头版头条，谭咏麟岳父去世，隐藏19年的感情被起底。\"},{\"type\":\"text\",\"value\":\"04、\"},{\"type\":\"text\",\"value\":\"杨洁薇处处维护谭咏麟的名誉，可是她不知道的时候，谭咏麟已经“金屋藏娇，频繁咏鹅 ”。\"},{\"type\":\"text\",\"value\":\"1995年，朱咏婷怀孕，纸包不住火的时候，谭咏麟只能与杨洁薇摊牌，杨洁薇很生气，但是也阻止不了事实的发展。\"},{\"type\":\"text\",\"value\":\"第二年，谭咏麟喜得贵子，“小老婆”想上位，但是杨洁薇却不想让位。\"},{\"type\":\"text\",\"value\":\"谭咏麟夹在两个女人之间，非常挠头，当媒体曝光谭咏麟与儿子的照片时，大家才知道谭咏麟“暗渡陈仓”地养了外妻。\"},{\"type\":\"text\",\"value\":\"谭咏麟对外界的声音选择听不到，看不见。\"},{\"type\":\"text\",\"value\":\"这段“三角虐恋”维持了十年。\"},{\"type\":\"text\",\"value\":\"2006年，谭咏麟的父亲去世，自己的两位“粉丝情人”出席，谭咏麟对着媒体说“我有个儿子，我妻子是杨洁薇，还有一个知己朱咏婷，她们有委屈，我也辛苦”。\"},{\"type\":\"text\",\"value\":\"主动承认二房的谭咏麟，把朱咏婷与儿子的名字写进了“讣文”中，但是没有杨洁薇的名字。\"},{\"type\":\"text\",\"value\":\"杨洁薇伤心欲绝，选择剃度出家。\"},{\"type\":\"text\",\"value\":\"但是，谭咏麟却说“自己家没有尼姑，也没有人出家”。\"},{\"type\":\"text\",\"value\":\"近几年，谭咏麟似乎消失在了大众的视线中。\"},{\"type\":\"text\",\"value\":\"就当大家以为谭咏麟由于身体原因，减少工作的时候，他又被爆出与“粉丝”有关系。\"},{\"type\":\"text\",\"value\":\"而且这个粉丝比他小了“48岁”。\"},{\"type\":\"text\",\"value\":\"71岁的谭咏麟还能“动”？\"},{\"type\":\"text\",\"value\":\"在爆料者的文章中，能看得出谭咏麟还是很“好动”的，与吴签、霍尊、钱枫，一样都是渣男。\"},{\"type\":\"text\",\"value\":\"不过，很快谭咏麟先生就发出了辟谣信息，也将这个“谣言”交给律师处理。\"},{\"type\":\"text\",\"value\":\"而后这位爆料者说“听说德艺双馨发声明了？”并配上一张疑似男主在酒店开房的背景图。\"},{\"type\":\"text\",\"value\":\"在这位爆料者的材料中，我们看不到一张正脸的照片，也没有石锤的旁证，所以谭咏麟睡粉这个事不能肯定。\"},{\"type\":\"text\",\"value\":\"但是在他的过往中，看得出，谭咏麟很喜欢与粉丝“互动”。\"},{\"type\":\"text\",\"value\":\"对此，您有何看法呢？欢迎在评论区留言讨论！\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"本文由文刀万原创，欢迎关注，带你一起长知识！\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"},{\"type\":\"text\",\"value\":\"(责任编辑：李超_NB12814)\"}]',0,6,'爬虫','2021-11-26 15:42:05','2021-11-26 15:42:05',2,'2021-11-26 15:42:05','文本内容包含自管理的敏感词: {66=1}',NULL,'',1),(6304,1102,'漫威新片引争议：黑人英雄下跪痛哭为广岛核爆忏悔','[{\"type\":\"text\",\"value\":\"观察者网11月25日报道 据日媒当地时间23日报道，最近上映的美国漫威电影《永恒族》中，出现了“黑人为日本广岛原子弹爆炸而忏悔跪地痛哭”的一幕，引发国内外网友激烈辩驳。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"编写出这段内容的是日裔编剧马修·弗雷波（Kaz Firpo），他希望借此让全球的观众去反思日本人民所遭受的悲剧，并在采访中批判了美国社会中存在根深蒂固的“投原子弹正当化”论。马修还表示，最初这段内容在剧组内部也引发极大争议，但华裔导演赵婷最终力排众议，将其保留了下来。\"},{\"type\":\"text\",\"value\":\"马修这番闭口不提是日本先发动的侵略罪行，只强调要反思日本人民所受痛苦的言论，在日本网民中获得大量支持。但在美国、韩国等国的一些网友眼中，这些无疑是在为侵略者“洗地”，挑战了大众一直所接受的反法西斯战争历史观。\"},{\"type\":\"text\",\"value\":\"漫威《永恒》日裔编剧谈广岛原子弹爆炸——“希望不再发生”\"},{\"type\":\"text\",\"value\":\"日裔美国人弗雷波兄弟谈对引起争议的广岛场景的执着\"},{\"type\":\"text\",\"value\":\"漫威新片《永恒族》讲述了一群和地球人长相相似但具有超能力的远古人类种族“永恒族”，他们一直秘密生活在地球，用超能力来推动人类文明发展。他们对于人类只能是观测者，但不能实际去干涉人类的行为。\"},{\"type\":\"text\",\"value\":\"片中设定人类所有的科技都是一个黑人同性恋超级英雄传授给人类的，但他发现人类制成原子弹轰炸了广岛。目睹了广岛原子弹爆炸的场面后，他跪在废墟前失声痛哭，“我不应该把科技传给人类，这样的悲剧就不会发生了。”被这一幕刺激到以后，黑人觉得人类不值得被帮助，于是选择归隐。\"},{\"type\":\"text\",\"value\":\"一手炮制出这段\\\"黑人广岛忏悔”剧情的是日裔编剧马修·弗雷波（Kaz Firpo），他是一名日美混血，拥有四分之一日本血统，爷爷是一名出生在日本的美国移民。\"},{\"type\":\"text\",\"value\":\"马修介绍自己编剧时的心路历程时称，小时候他在加州学习原子弹的有关历史时，班上有过半的同学支持对日本投放原子弹。作为日裔血统的美国人，他为同学们“替这一恐怖举动辩护”感到难受，他希望借助这个片段促使观众“重新思考如何不让这一糟糕的事情重现”，去反思日本平民在这一事件中所遭受的痛苦。“这是人类的悲剧，本来不应该发生，在原子弹下面，日本人民也是战争的受害者。”\"},{\"type\":\"text\",\"value\":\"“作为日裔美国人，作为在日本有家人的人，这在我的人生和历史中占据了很大的一部分。我们不应该回避人类的悲剧，它不应该再发生第二次。”“这是电影里最能打动人心的地方，就是希望世界上所有的人，不分种族、出身、地区，都来思考这件事情，到底做得对不对。”\"},{\"type\":\"text\",\"value\":\"同时，马修也认为“是否应该向广岛投原子弹”这个问题的答案处于灰色地带，没有标准答案，并在采访中批判了美国社会中存在根深蒂固的“投原子弹正当化”论。\"},{\"type\":\"text\",\"value\":\"他说，在美国很多人都同意是投放了原子弹才让二战尽快结束。因为原子弹爆炸导致日本投降，美军的日本登陆战就没有实施，从而避免了100万美军士兵的伤亡。“投了原子弹就能拯救100万人的生命吗？这个谁也不知道。”\"},{\"type\":\"text\",\"value\":\"马修还透露，其实这个设定在前期讨论剧本时就在内部产生了极大的争议，尤其是负责海外发行的电影公司担心会带来很多不必要的麻烦，也有人强烈要求去除这一桥段。在众人争执不下时，是华裔导演赵婷最终力排众议，将其保留了下来，这也让马修直赞她是“极具人文关怀”。\"},{\"type\":\"text\",\"value\":\"《永恒族》编剧马修·弗雷波（Kaz Firpo）与瑞恩·弗雷波（Ryan Firpo），两人是表兄弟\"},{\"type\":\"text\",\"value\":\"马修这番言论被日媒蜂拥报道，日本网民的评论区里也是支持声一片，夸赞他为日本广岛核爆受害人民发声。他们认为马修设计的这一桥段只是单纯对原子弹这一“无差别杀伤”的武器进行反思。\"},{\"type\":\"text\",\"value\":\"有网友对日本网民的评论大为恼火：你想知道日本人对广岛场景的真实想法吗？你看！\"},{\"type\":\"text\",\"value\":\"但在美国、韩国等国的一些网友眼中，大家普遍认为马修闭口不提是日本先发动的侵略罪行，对各国、尤其是东亚国家造成的罪行罄竹难书，还只强调要人们反思日本广岛人民所受痛苦的言论，是在为侵略者“洗地”，回避历史的真相，挑战了大众一直所接受的反法西斯战争历史观。\"},{\"type\":\"text\",\"value\":\"韩国网友火力全开：“二战里这么多惨剧，永恒族只心疼广岛，被日本法西斯戕害的任何一方都没能入它的眼。”“这部电影竟然将日本置于（二战）受害者的角度？”\"},{\"type\":\"text\",\"value\":\"韩国网友：“来自全亚洲的呼声：相信我，你们就该被核武器轰。”\"},{\"type\":\"text\",\"value\":\"一部分美国网友还关注到马修将向广岛投弹的责任扣在了一个黑人的头上，“你知道有什么比广岛核爆能让法斯托斯（黑人永恒族）更崩溃的吗？让他看到他发明的犁技术最终被用来奴役黑人。”“永恒族看了几个世纪的奴隶制和殖民化，结果广岛核爆让他对人类绝望了……”\"},{\"type\":\"text\",\"value\":\"有网友犀利直言，影片中竭尽全力保留这一片段恰恰是一种“伪善”，“漫威不愿意正视或谴责真正的邪恶。”\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(原标题：日裔编剧设定漫威新片《永恒族》引争议：黑人英雄下跪痛哭，为广岛核爆忏悔)\"},{\"type\":\"text\",\"value\":\"(责任编辑：蒋紫莹_NBJS16943)\"}]',0,6,'爬虫','2021-11-26 15:42:06','2021-11-26 15:42:06',2,'2021-11-26 15:42:06','文本中有违规内容，审核失败',NULL,'',1),(6305,1102,'“安嘉和”冯远征在国际反家暴日发声：勇敢说不','[{\"type\":\"text\",\"value\":\"网易娱乐11月25日报道 “只有我能受得了你”“我这么做都是被你逼的”“我对你太失望了”……这些话，你经常听到吗？11月25日是国际反家庭暴力日，冯远征提醒要警惕5种精神暴力，对暴力勇敢说不，拒绝伤害！\"},{\"type\":\"text\",\"value\":\"视频中，分别提到了应警惕的5种精神暴力：1、诱导扭曲：应警惕用语言反复引导，让你产生自卑和依赖的情绪；2、推卸责任：发生责任时，TA把责任全推给你，让你自我否定；3、超强控制欲：猜疑心重，控制欲强，并试图处处控制你的行为；4、羞辱打击：TA用侮辱性的语言，打击羞辱你摧毁你的自信；5、要挟威胁：TA经常要挟你，有时看似示弱，其实TA是在利用你的负罪感来操控你。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"此前，冯远征在电视剧《不要和陌生人说话》中饰演男主角安嘉和，该剧是中国第一部直观反映家庭暴力的影视作品，瞄准暴力家庭成员的心理和心态上的微妙变化，讲述医界名流安嘉和与美貌妻子梅湘南新婚燕尔之时，丈夫对妻子的言行难以信任，深深的爱恋由此转化为苛刻的束缚的故事。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"该电视剧在当时给不少人留下了童年阴影。有网友表示：“这条找冯远征老师来拍真的太适合不过了”，“看到冯远征老师就想到安嘉和，塑造的家暴形象太成功了。”\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：刘舒阳_NBJS16803)\"}]',0,6,'爬虫','2021-11-26 15:42:07','2021-11-26 15:42:07',9,'2021-11-26 15:42:07','阿里云调用失败，转人工审核',1464138459789680642,'',1),(6306,1102,'《战狼2》发图庆《长津湖》登顶华语电影票房冠军','[{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"网易娱乐11月25日报道 据猫眼专业版，截至25日发稿，电影《长津湖》票房达56.94亿，超过了《战狼2》登顶中国影史票房榜。\"},{\"type\":\"text\",\"value\":\"今日《战狼2》官微发长图祝贺《长津湖》登顶华语电影票房冠军。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"电影《长津湖》自9月30日上映后就受到了影迷的喜爱，部分版本密钥二次延期，期限为12月1日至12月30日。《长津湖》由陈凯歌、徐克、林超贤联合执导，吴京、易烊千玺领衔主演，段奕宏特别出演，朱亚文、李晨、胡军、韩东君等主演的抗美援朝电影。\"},{\"type\":\"text\",\"value\":\"相关报道： 有续集!《长津湖之水门桥》官宣 大部分内容已拍完\"},{\"type\":\"text\",\"value\":\"网易娱乐10月28日报道 10月28日，电影《长津湖之水门桥》发布先导海报，第七穿插连的战士们将要在美军撤退路线上的咽喉之处——水门桥，与对方再次展开一场殊死搏斗。\"},{\"type\":\"text\",\"value\":\"该片以抗美援朝战争第二次战役中的长津湖战役为背景，讲述在结束了新兴里和下碣隅里的战斗之后，七连战士们又接到了更艰巨的任务……\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"据总制片人、博纳影业集团董事长于冬透露，《长津湖之水门桥》的大部分内容已经在今年年初拍摄完毕，由于季节变换的原因，有些戏份将延续到冬天拍摄。今年入冬后，全体主创将再度集结，继续拍摄完余下的部分。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：李思_NBJ11322)\"}]',0,6,'爬虫','2021-11-26 15:42:08','2021-11-26 15:42:08',9,'2021-11-26 15:42:08','阿里云调用失败，转人工审核',1464138459949064193,'',1),(6307,1102,'许鞍华说不会再拍张爱玲：观众不会想我再拍第四次','[{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"中国新闻网11月25日报道 由香港导演许鞍华执导的电影《第一炉香》即将于11月25号在香港上映。此前，这一部改编自张爱玲成名作的影片在中国内地上映后出现争议。因前两次执导张爱玲作品的表现都可圈可点，“三顾”张爱玲，74岁的许鞍华无疑承担着过高的期待。\"},{\"type\":\"text\",\"value\":\"日前，她在接受中新社记者专访时表示，如今的她已能坦然接受外界的评价。她直言，张爱玲是一位非常理想的作家，但三次拍摄经历于她而言已然足够，更表示“我估计观众也不想我再拍第四次了。”\"},{\"type\":\"text\",\"value\":\"相关报道： 《第一炉香》票房扑街 张爱玲作品被大师班底拍成疼痛文学\"},{\"type\":\"text\",\"value\":\"【网易娱乐 影视 原创栏目《超有戏》，每部好片背后，都有人与时代的变迁】\"},{\"type\":\"text\",\"value\":\"上世纪三四十年代的香港总是有着许多故事的，所谓上流社会披着纸醉金迷的外皮，暗里涌动着糜烂的各色欲望，豪门大家的桃色流言最为人津津乐道。\"},{\"type\":\"text\",\"value\":\"张爱玲的《第一炉香》就写在这里，一位风姿绰约的孤寡姨太太，领着一位青春正好的侄女，在男人和金钱之间来来回回，从她的笔下描画出残破的命运。\"},{\"type\":\"text\",\"value\":\"著名小说改编电影，配上了一流的制作班底，导演许鞍华、编剧王安忆、摄影指导杜可风、音乐总监坂本龙一，皆是业内响当当有品质保障的大佬。\"},{\"type\":\"text\",\"value\":\"偏是这样一群人的携手之作，刚上映的《第一炉香》竟翻车得彻底，豆瓣5.5分骂声四起，上映11天票房才刚破5000万。\"},{\"type\":\"text\",\"value\":\"葛薇龙是原居上海的清纯女学生，为躲避战事举家迁到香港生活了几年，家里逐渐捉襟见肘，眼下要么留在香港为学费发愁，要么跟着父母搬回上海。思及前途，她咬咬牙决定去向那个当人家姨太太的姑妈求援。\"},{\"type\":\"text\",\"value\":\"那是住在香港半山上“梁宅”的寡妇，年轻貌美偏要为了钱嫁给大龄富商做小，为此和家人闹翻断联。\"},{\"type\":\"text\",\"value\":\"熬到枕边人离世，凭着生前经营的宠爱分了一大笔遗产，得人一声“梁太太”称呼，自此在这一场又一场的浮华宴会中施展长袖善舞的本事。\"},{\"type\":\"text\",\"value\":\"01|选角\"},{\"type\":\"text\",\"value\":\"“人人眼睛里有了我就不能有第二个人”，这位眼梢都带着媚丝的姑妈梁太太选了俞飞鸿来演，算是刚刚及格。\"},{\"type\":\"text\",\"value\":\"而男女主角的选择才是让电影失败的主要原因之一。\"},{\"type\":\"text\",\"value\":\"故事围绕着葛薇龙展开，她住进华贵如艺术品的梁宅中，感受了从未体验过的“上流生活”。\"},{\"type\":\"text\",\"value\":\"在珠宝华服里葛薇龙挣扎过，最后还是放纵自己陷进物欲。她选择爱上个没有心的男人——乔琪乔，硬是要来了一段没有忠诚可言的婚姻，也给自己找来成为下一个“梁太太”的理由。\"},{\"type\":\"text\",\"value\":\"本该在大荧幕上看到一个痛苦又得意，倔强又狡黠的葛薇龙，共情她一步一步从被迫到甘愿的内心活动。\"},{\"type\":\"text\",\"value\":\"可葛薇龙的扮演者选择了马思纯，她无疑是片中最大的败笔。\"},{\"type\":\"text\",\"value\":\"在整部电影中，马思纯没能演出葛薇龙的复杂心性，她情绪单一，也都流于表面。姑且不论改编问题，单从电影的剧情来说，所有的内容马思纯都演了，却只演了一层。\"},{\"type\":\"text\",\"value\":\"原是个内心复杂的小姑娘，她能为自己谋划未来，有勇气有决断，敢自己留在香港找出路，独自对着人精一样的姑妈能委得下身。\"},{\"type\":\"text\",\"value\":\"马思纯所演的葛薇龙就只是个稚气的女学生，来恳求姑妈时倒是低眉顺眼了，背后的小忐忑、小算计和聪明劲儿全无，只留下一脸憨傻无辜。\"},{\"type\":\"text\",\"value\":\"在电影中葛薇龙接受了姑妈的帮助，偏偏又有点傲气，不甘走“梁太太”的老路，一边勤奋学习，一边应付姑妈，一边还得物色另一半。\"},{\"type\":\"text\",\"value\":\"物色了一个穷大学生卢兆麟，还没来得及深入发展，先被姑妈“勾”了去。\"},{\"type\":\"text\",\"value\":\"葛薇龙是什么心情？该是能理解卢兆麟想“向上”的心思，又为他和梁太的事略为不齿，也为自己丢了一个预备对象暗自气愤，但马思纯演得几乎毫无波澜。\"},{\"type\":\"text\",\"value\":\"对男主乔琪乔一见钟情，拼了命的想要嫁给他，马思纯演出了葛薇龙的少女情怀，可总撇着嘴夹着一丝别扭。\"},{\"type\":\"text\",\"value\":\"葛薇龙知道乔琪乔不会娶自己，还是和他在深夜有了一场欢爱。谁知道这个男人才刚离开房间又和自己的丫鬟睡了一觉，清晨醒来撞破这个画面讽刺又绝望。\"},{\"type\":\"text\",\"value\":\"遭到如此背叛，马思纯所演绎的痛苦就是抱膝坐在地上双目无神，任谁看了都是一副失足少女的做派。\"},{\"type\":\"text\",\"value\":\"自此，马思纯开启了一系列的“外露式演技”，歪着脑袋似笑非笑，或是满脸茫然和忧郁，或是歇斯底里的咆哮质问。观众可以一眼就分辨出马思纯最想呈现的心情，直白到像是初学级演员，没有层次和递进。\"},{\"type\":\"text\",\"value\":\"葛薇龙为了养乔琪乔这个软饭男放弃了自己的清高，在后期选择和姑妈梁太太一起当香港半山豪宅的交际花，饶是这样的麻木转变，还是被马思纯演出了几分滑稽感，就好像小朋友偷穿大人的衣服般不自如。\"},{\"type\":\"text\",\"value\":\"马思纯演砸了葛薇龙，彭于晏也没演好男主角，乔琪乔。和原著里那个没有血色、带点“丫头气”的混血儿相比，彭于晏的健康和阳刚气扑面而来。\"},{\"type\":\"text\",\"value\":\"他在外形上实在是不配乔琪乔，好在演起渣男倒也没那么违和，流恋在各色女人之中，一副吊儿郎当的模样。\"},{\"type\":\"text\",\"value\":\"可乔琪乔就只是个简单的渣男吗？\"},{\"type\":\"text\",\"value\":\"他早年丧母又不讨父亲喜爱，有十几个兄弟姐妹，纵然家境显赫却分不到他手里多少。凭着一张好脸蛋，他立志要“嫁”个有钱小姐当“驸马”，惯是颓废又浪荡的。\"},{\"type\":\"text\",\"value\":\"彭于晏演的呢？是情场浪子没错，还多了几分油腻。他演出了一脸肆意和坏笑，没能透露一点背后的落寞和可怜，乔琪乔也有自己的矛盾之处。\"},{\"type\":\"text\",\"value\":\"不情不愿娶了葛薇龙，看见她为了搞钱要和“老板”去上海出差，乔琪乔忽然不乐意了，抱住行李箱就开始耍赖，遮住对面的马思纯，怀疑彭于晏是在对妈妈撒娇也一定有人信。\"},{\"type\":\"text\",\"value\":\"在片尾，面对葛薇龙顾影自怜式的感情质问，彭于晏倒像是没恋爱过的毛头小子不知所措，这对男女主角的演技实在是没能撑起这部电影，也衬不了张爱玲写的人物。\"},{\"type\":\"text\",\"value\":\"02|剧情\"},{\"type\":\"text\",\"value\":\"《第一炉香》可以算是对原著有着极高的保留程度，台词和情节都在尽可能还原，问题出在哪？\"},{\"type\":\"text\",\"value\":\"张爱玲写下这篇短篇小说，在短小的篇幅中用细腻的文字润色埋伏了每一处跳跃和转折。\"},{\"type\":\"text\",\"value\":\"“给他那双绿眼睛一看，她觉得她的手臂像热腾腾的牛奶似的，从青色的壶里倒了出来，管也管不住，整个的自己全泼出来了”、“每逢她出去应酬，不论是什么集会，总有他在座”。这样的一见钟情和感情积攒在王安忆的改编中没有呈现。\"},{\"type\":\"text\",\"value\":\"搬到电影里来，葛薇龙对乔琪乔的爱来得没头没尾，眼神还没来得及痴缠就已经默认是爱到病态了。\"},{\"type\":\"text\",\"value\":\"知道对方不爱自己，还是选择嫁给他，其实葛薇龙在张爱玲的笔下婚前就已经想得再明白不过。可王安忆让她婚后还在为爱崩溃，加了很多情节，自暴自弃去赌钱喝酒抽烟，对着一再出轨的乔琪乔扇耳光泄愤，渣男还让她放过彼此。\"},{\"type\":\"text\",\"value\":\"葛薇龙才不是个不清醒的恋爱脑，更不会像王安忆在片尾安排的那样，对着车外大喊“我爱你，你个没良心的”。\"},{\"type\":\"text\",\"value\":\"葛薇龙和乔琪乔被简化成了爱而不得的痴男怨女，生硬得让观众发笑，一时不知道该怪两位主演演技不够到位，还是该怪编剧安排不够合理。\"},{\"type\":\"text\",\"value\":\"王安忆还给姑妈梁太太加了两场回忆的戏。一场是她给富商当四姨太，进门时站着给三位“姐姐”敬茶没人喝，只能跪下。另一场是送葬路上高跟鞋坏了，她把鞋脱了之后转身离开。\"},{\"type\":\"text\",\"value\":\"两个往日画面丰满了梁太太的人物形象，用得好就是锦上添花，但在主线尚且逻辑不足的情况下，这个改动有点喧宾夺主了。\"},{\"type\":\"text\",\"value\":\"03|画面\"},{\"type\":\"text\",\"value\":\"《第一炉香》就没有一点可取之处吗？\"},{\"type\":\"text\",\"value\":\"作为华语影坛最著名的女性电影人之一，许鞍华自然有独到之处，在她的掌控下，整部影片的画面精致华美。\"},{\"type\":\"text\",\"value\":\"无论是对人物的运镜，还是景色的构图、色调，随手一截都是海报级别。\"},{\"type\":\"text\",\"value\":\"葛薇龙迈入上山拜访的拐角阶梯时，就走进了再也回不去的地方，满眼葱郁的绿色还带着一丝引诱的暗示。\"},{\"type\":\"text\",\"value\":\"葛薇龙和乔琪乔在初次见面时坐在一根倒地树枝上闲谈，两个人从这次见面后就注定绑在了一起，画面饱含隐喻又唯美。\"},{\"type\":\"text\",\"value\":\"在豪宅之外的乔琪乔也不过是不受人重视的一个浪荡子，拉远的镜头把他和恢弘的建筑形成了鲜明的对比。\"},{\"type\":\"text\",\"value\":\"这都是镜头的语言与力量，也是许鞍华的高级所在。\"},{\"type\":\"text\",\"value\":\"这不是许鞍华第一次拍张爱玲的作品。\"},{\"type\":\"text\",\"value\":\"早在1984年，她就执导了周润发版的《倾城之恋》。\"},{\"type\":\"text\",\"value\":\"1997年，许鞍华再次执导了由黎明主演的《半生缘》。\"},{\"type\":\"text\",\"value\":\"算上这次，是第三次翻拍了，也是她在张爱玲作品上的第三次票房扑街。\"},{\"type\":\"text\",\"value\":\"许鞍华说《第一炉香》首先是部爱情片，从前期宣传来看，的确所有的海报和宣传文案都围绕在“爱而不得”的营销中。\"},{\"type\":\"text\",\"value\":\"这恰恰是被吐槽得最狠的地方，张爱玲的作品被看狭隘了。\"},{\"type\":\"text\",\"value\":\"葛薇龙被投射了那个年代女性的无奈和沉沦，没有好的就业环境和独立资格，必须依靠婚姻，不能把她看成爱而不得的傻子。\"},{\"type\":\"text\",\"value\":\"“我爱你，关你什么事？千怪万怪，也怪不到你身上去。”\"},{\"type\":\"text\",\"value\":\"“公平？人与人之间的关系里，根本谈不到公平两个字。”\"},{\"type\":\"text\",\"value\":\"“怎么没有分别呢？她们是不得已，我是自愿的。”\"},{\"type\":\"text\",\"value\":\"会说出这些话的葛薇龙太清楚自己的命运和选择了，不嫁给乔琪乔又可以选谁呢？张爱玲洞察的是人性和时代，笔触冷静又疏离，爱情只是每个故事的穿珠线，读者悟的是背后的真实。\"},{\"type\":\"text\",\"value\":\"《第一炉香》的故事放到今天依然成立，把一个人从下阶层骤然扔到上阶层，任谁也不能轻易舍去，这是今天的“葛薇龙们”还在追求的物欲，还是充满了讽刺和揭露的意味，这就是张爱玲的魅力。\"},{\"type\":\"text\",\"value\":\"许鞍华也没能免俗把女性拍得太简单太蠢了，把人性看得太弱了，在大多数人的世界里，爱情只是个可替代的幌子。\"},{\"type\":\"text\",\"value\":\"纯爱电影会受欢迎正是因为歌颂了难能可贵的东西，把本就不是纯粹爱情的《第一炉香》硬改成纯爱故事反而显得不伦不类，格局变小了。\"},{\"type\":\"text\",\"value\":\"反观近年来备受好评的作品，无论是电视剧还是电影，爱情都只是一小部分。《都挺好》、《隐秘的角落》、《82年生的金智英》、《寄生虫》、《山海情》等等，各色的题材都在深入聚焦现实问题和矛盾，在探讨或反映人性、阶层、社会或时代，更贴近每一个正在生活的人。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"希望华语影坛的导演们不要再把视角只放在小情小爱上了，观众们已经疲惫了，也不需要。\"},{\"type\":\"text\",\"value\":\"(责任编辑：李思_NBJ11322)\"}]',0,6,'爬虫','2021-11-26 15:42:09','2021-11-26 15:42:09',2,'2021-11-26 15:42:09','文本中有违规内容，审核失败',NULL,'',1),(6308,1102,'Jessie J发长文称自己流产 虽难接受但会完成演出','[{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"网易娱乐11月25日报道 英国流行女歌手JessieJ在个人社交账号中发布手持验孕棒的自拍，并表示自己流产了。\"},{\"type\":\"text\",\"value\":\"在配文中她写道：她还未告诉大家自己怀孕的消息，本来还在担心接下来的演出中如何不被人看出来。但是现在孩子已经离开了，感觉情绪控制不住，自己也还在接受这个事实中。但为了观众、为了自己和孩子，今晚仍会在洛杉矶完成好演出。\"},{\"type\":\"text\",\"value\":\"她还提到在第三次产检中，突然发现宝宝已经没有心跳。她求助医生，但是医生也无能为力，悲痛的心情不言而喻。\"},{\"type\":\"text\",\"value\":\"2018年1月，Jessie J加盟中国湖南卫视节目《歌手2018》。4月13日，Jessie J夺下《歌手2018》总冠军，首个“外国歌王”诞生，也在中国获得了大量的观众喜爱。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：李思_NBJ11322)\"}]',0,6,'爬虫','2021-11-26 15:42:10','2021-11-26 15:42:10',9,'2021-11-26 15:42:10','阿里云调用失败，转人工审核',1464138460112642049,'',1),(6309,1102,'大S不受离婚影响，与小S节目《熙娣想聊》定档','[{\"type\":\"text\",\"value\":\"《熙娣想聊》定档\"},{\"type\":\"text\",\"value\":\"小S的《熙娣想聊》官宣定档了，将于12月23日播出，每周四更新最新集数，与十位不同来宾激荡出前所未见的火花，小S说：“我希望呈现给观众最真实、最赤裸、最勇敢、最做自己的我，不论你在哪个地方，都希望能透过『熙娣想聊』得到欢乐与疗愈。”\"},{\"type\":\"text\",\"value\":\"这档节目不同于《康熙来了》和《花花万物》，从名字就可以看出来也是一档访谈节目，不过形式不太一样，会出外景以访谈的方式来录制，这也是大小S的再次合作，大S还是这档节目的制片人，大小S姐妹合体做节目，真是令人期待。\"},{\"type\":\"text\",\"value\":\"小S对这次与大S的合作也比较重视，对出外景也没有什么要求，甚至说穿拖鞋出镜也愿意，小S休闲了这么长时间，又到了大展身手的时候了，自然也是很兴奋，所以才会超配合吧。\"},{\"type\":\"text\",\"value\":\"观众期待已久的原因：大小S的主持风格有趣\"},{\"type\":\"text\",\"value\":\"大小s合体录制过很多综艺，如《大小爱吃》、《娱乐百分百》、《我们是真正的朋友》等，姐妹俩的主持风格有趣，都深受观众喜爱，从去年传出要做这档节目时，网友们就很关注，现在终于要来了，网友们直呼：“期待已久了”。\"},{\"type\":\"text\",\"value\":\"两人很小的时候以“徐氏姐妹”的名号出道，后来又组成歌唱组“ASOS”，之后再次转行做主持，姐姐大S把精力主要放在拍戏上，成为内地和台湾都很知名的演员，主演了《流星花园》风靡全国。\"},{\"type\":\"text\",\"value\":\"妹妹小S成为台湾数一数二的女主持人，主持了最红访谈节目《康熙来了》，小s的主持功力是非常不错的，她很会聊天，有在认真听来宾说话，张弛有度的在娱乐和感人之间游离，不显尴尬，不抢风头，也够真诚有时候问的问题很尖锐但是圆的好，不会冷场。\"},{\"type\":\"text\",\"value\":\"大S小S的配合也很好，经常两个人在不知不觉间就能，逼供出来宾一些比较有料的新闻，两一刚一柔的方式配合得特别好，有些明星讲话特别官方，经常能被姐妹俩从特别的角度挖出新的东西来，没有抢话的感觉。\"},{\"type\":\"text\",\"value\":\"小S首度单飞主持的网综《姐姐好饿》，首播当天就创下了超高的播放记录，并引发外界热赞，很多网友直呼，她强大的控场能力以及真性情的S式访问，也让“没有蔡康永帮扶，小S独立主持能力不足”的质疑不攻自破。\"},{\"type\":\"text\",\"value\":\"很多网友就很喜欢听大S聊天，不管什么事都说的很吸引人而且对一些人事的用词格外清奇有趣，大s组织能力非常强，给人一种有张有弛，娓娓道来，不疾不徐的感觉，每次听她说话都很舒服，不疾不徐，条理清晰。\"},{\"type\":\"text\",\"value\":\"大S不受离婚影响\"},{\"type\":\"text\",\"value\":\"大S在离婚之后，确实霸占了很长时间的热搜，网友们非常关注大S和汪小菲离婚一事，相较于那些离婚时比较狗血的明星夫妻，大S和汪小菲的离婚确实非常体面，并且大S也平静地表示，她希望小菲一直过得都比自己好。\"},{\"type\":\"text\",\"value\":\"官宣离婚后，台媒爆料大S被拍到现身餐馆喝酒，一个人独自享用白松露大餐，告诉服务员“若不喊停就继续刨白松露”最后刨了一万多，红酒配上白松露，绝对是别样的享受，看来大S心情并没受到离婚的影响。\"},{\"type\":\"text\",\"value\":\"小S透露大S瘦身成功，就连手臂都变得很细，现在完全变成杉菜的样子，很多网友都觉得大S一直在逆生长，越来越有少女感了，看样子她在离婚之后的心情确实非常平静。\"},{\"type\":\"text\",\"value\":\"如今大S和汪小菲的身家都不菲，即使两人离婚了，身边也不会缺少桃花，相信终有一日，他们能够找到真正属于自己的另一半，期待大小S的综艺《熙娣想聊》的播出，网友们都期待很久了。\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:42:12','2021-11-26 15:42:12',9,'2021-11-26 15:42:12','阿里云调用失败，转人工审核',1464138460242665474,'',1),(6310,1102,'宋祖儿人工呼吸花絮曝光！被按胸口吐男演员一脸水','[{\"type\":\"text\",\"value\":\"近日，宋祖儿拍戏期间的花絮视频被曝光，当天宋祖儿拍摄一组落水戏，拍摄地点正好位于一个小池塘边上，演员们拍摄也相当卖力认真，落水和营救的戏份看起来都相当真实。\"},{\"type\":\"text\",\"value\":\"其中有一段戏需要宋祖儿被王安宇做人工呼吸，两人都表现得相当专业，看起来就像是现场教学一般。\"},{\"type\":\"text\",\"value\":\"当宋祖儿被王安宇大力按压胸口时，本来需要装晕的宋祖儿突然憋不住了，直接当着镜头被压到弹跳了起来，想必应该是男方力气太大，所以宋祖儿一时无法忍受，于是在当下做出了最真实的反应。\"},{\"type\":\"text\",\"value\":\"因为是闭着眼睛的原因，宋祖儿还直接把自己提前含在嘴里的水，直接吐到了王安宇的脸上，画面看起来完全就是一出喜剧。\"},{\"type\":\"text\",\"value\":\"两人抬起头的时候，彼此脸上都是宋祖儿吐出来的口水，画面看起来简直不要太搞笑。\"},{\"type\":\"text\",\"value\":\"甚至连一旁拍摄的镜头都没能够幸免于难，直接被宋祖儿吐的水，淋了个透，一旁的工作人员们也只能在结束拍摄后，拿着纸巾紧急处理，现场看起来一片慌乱。\"},{\"type\":\"text\",\"value\":\"虽然目前这部戏还没有开播，但是从不断被曝光的花絮来看，男女主角都为此付出了不少的努力\"},{\"type\":\"text\",\"value\":\"因为角色定位为专业运动员的关系，宋祖儿在幕后花了不少的时间学习新技能，力求能够达到符合原著的水平，但过程却比大家想象的要艰难许多。\"},{\"type\":\"text\",\"value\":\"看到宋祖儿多次穿着单薄地衣服摔倒在地上，也属实是有些令人心疼，但演员这个工作的独特性也就在于此，为了能够呈现出最真实最精彩的表演，演员们就需要在幕后接受千锤百炼。\"},{\"type\":\"text\",\"value\":\"影视作品镜头中所有“撒糖”和“耍帅”的画面，都是在片场所有工作人员们配合演绎才呈现出来的，或许并没有大家想象中的梦幻，但却能够看到艺人们的努力和专业。\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:42:13','2021-11-26 15:42:13',9,'2021-11-26 15:42:13','阿里云调用失败，转人工审核',1464138460452380674,'',1),(6311,1102,'赵本山女儿这是怎么了，才24岁就脱发这么严重？','[{\"type\":\"text\",\"value\":\"这造型着实吓了我一跳，超宽的额头、稀少的发量，你要不说她是赵本山的女儿球球，我还以为是六七十岁的大妈。如果没有美颜的加持，我觉得球球的难看程度还会提升很多，这是咋了？明明才24岁的小姑娘啊！怎么这脱发的困扰她也有呢？据悉，她还专门去植发了，这次直播就是给我们看看效果。\"},{\"type\":\"text\",\"value\":\"在我的印象里，球球每一次出现，我都觉得她有一点奇怪。后来琢磨了一下，感觉她的头发发量不足，而且有一些发黄，不是特意染的那一种，而是类似于农村营养跟不上的小孩那种。可她却是小品之王赵本山的女儿啊，差钱这件事显然是不存在的。一出生就含着金汤匙，球球的生活条件远超我们绝大多数人。\"},{\"type\":\"text\",\"value\":\"在直播中，我们清晰可见，球球的脱发到了一个令人惊讶的地步。她的前额宽度已经快接近整个头顶的三分之一了，也就是说整体头发向后移动。在存在的头发中，我们很容易就看到头皮，说明她仅有的一些头发的发量也出奇得少。如果我们能翻到之前的视频的话，会发现这种现象在愈演愈烈中。\"},{\"type\":\"text\",\"value\":\"在球球还没有开直播之前，我们偶尔可以看到她和赵本山的合照。那时候，她一般都是用齐刘海来遮挡住额头，我们看不到她的额头，自然也不知道发际线后移这件事。后来，她开直播之后，发现头发挡在眼前比较碍事，就把刘海剪掉了。只是，她一次又一次的直播，她的发际线就一次又一次的后移。\"},{\"type\":\"text\",\"value\":\"发际线后移这件事，一般都发生在50岁往上的男性人群中，就算个别提前了，那也起码要30岁，很少听说有20几岁就秃的，更何况球球还是一个女的。反倒是赵本山的儿子牛牛没有这个困扰，几次出现都可以看到他的发量挺足。关键点是，老赵和他媳妇也没有任何脱发的嫌疑啊。\"},{\"type\":\"text\",\"value\":\"有的网友看到之后，说球球真像那个《神雕侠侣》中的裘千尺，这么一说我也觉得像，特意把原版照片拿了出来对比了一下。发际线后移的位置真的和裘千尺很像，但人家那是拍戏，用的假发套，而球球这是现实版的。植发这个东西，只能说缓解一下糟糕的外形，但不可能恢复到从前了。\"},{\"type\":\"text\",\"value\":\"而且，植发这个东西特别贵，尽管老赵家不差钱，但球球也必须要受到皮肉之苦。究竟是什么导致了这个现象呢？球球天生性格就直爽，个人觉得挺像一个爷们，也属于新时代典型少年。一些新时代的不好习惯她都要沾点边，重点是熬夜这一条。主播一般都是夜里直播，这就需要用本该休息的时间来替换。\"},{\"type\":\"text\",\"value\":\"除了遗传因素之外，熬夜是造成脱发的很重要的一个原因。球球出道了几年，她就直播了几年，然后再和朋友们夜里出去玩，这种颠倒黑白的生活加速了她的脱发，让一个仅有24岁的小姑娘就饱受中年男人的苦恼。这么说吧，只要她自己不重视这件事，这种脱发的现象会更加严重，植发是根本解决不了问题的。\"},{\"type\":\"text\",\"value\":\"特别声明：以上内容(如有图片或视频亦包括在内)为自媒体平台“网易号”用户上传并发布，本平台仅提供信息存储服务。\"}]',0,6,'爬虫','2021-11-26 15:42:14','2021-11-26 15:42:14',9,'2021-11-26 15:42:14','阿里云调用失败，转人工审核',1464138460741787649,'',1),(6312,1102,'意外撞到头部还有红肿？昆凌回应：好多了','[{\"type\":\"text\",\"value\":\"网易娱乐11月25日报道 25日，昆凌在个人社交平台发布动态报平安：谢谢大家的关心，我头好多了。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"据悉，23日昆凌在当天下午突发意外撞到头部。经纪人向台媒回应表示，有到她家探望，她的头部些微红肿，身体较疲倦，还好意识清楚，不过经纪人想让她多休息并未细问她怎么撞到，可能是照顾小朋友或做家务时不小心撞到。\"},{\"type\":\"text\",\"value\":\"\"},{\"type\":\"text\",\"value\":\"(责任编辑：韩冲_NBJ11345)\"}]',0,6,'爬虫','2021-11-26 15:42:15','2021-11-26 15:42:15',9,'2021-11-26 15:42:15','阿里云调用失败，转人工审核',1464138460917948417,'',1),(6313,1102,'金晨粉色吊带开衩长裙撩人 大秀优越肩颈线','[{\"type\":\"text\",\"value\":\"测试文章内容\"}]',0,6,'爬虫','2021-11-26 15:42:16','2021-11-26 15:42:16',9,'2021-11-26 15:42:16','阿里云调用失败，转人工审核',1464138461068943362,'',1),(6314,1102,'测试发布文章能否加入es','[{\"type\":\"text\",\"value\":\"dfsdfsd\"},{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/8164badb0c174151a8807b1d6d360522.jpg\"}]',1,46,'测试','2021-12-03 11:31:24','2021-12-03 11:31:24',9,'2021-12-03 11:27:54','审核通过',1466610229394309121,'material/2021/11/20211123/8164badb0c174151a8807b1d6d360522.jpg',0),(6315,1102,'呵呵关键字','[{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/b8cc1924f6774cf195e09c35d234835b.jpg\"},{\"type\":\"image\",\"value\":\"http://hmtt135.oss-cn-shanghai.aliyuncs.com/material/2021/11/20211123/72ba84a84f0e461e865b737759d1ddf4.jpg\"}]',1,6,'明星','2021-12-03 15:25:43','2021-12-03 15:25:43',3,'2021-12-03 15:25:33','图片中有不确定因素，需要人工审核',NULL,'material/2021/11/20211123/b8cc1924f6774cf195e09c35d234835b.jpg',1);

/*Table structure for table `wm_news_material` */

DROP TABLE IF EXISTS `wm_news_material`;

CREATE TABLE `wm_news_material` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `material_id` int(11) unsigned DEFAULT NULL COMMENT '素材ID',
  `news_id` int(11) unsigned DEFAULT NULL COMMENT '图文ID',
  `type` tinyint(1) unsigned DEFAULT NULL COMMENT '引用类型\r\n            0 内容引用\r\n            1 主图引用',
  `ord` tinyint(1) unsigned DEFAULT NULL COMMENT '引用排序',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=361 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci ROW_FORMAT=DYNAMIC COMMENT='自媒体图文引用素材信息表';

